import React, { useState } from 'react';
import {
  Box,
  Card,
} from '@mui/material';
import { Book, Edit, Users } from 'lucide-react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import HeaderComponent from '../../component/header/HeaderComponent';
import { colors } from '../../constants';

const CreateForumChallenge = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('READING_CHALLENGE');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    type: 'READING_CHALLENGE',
    startDate: '',
    endDate: '',
    targetGoal: '',
    reward: '',
  });

  const challengeTypes = [
    {
      type: 'READING_CHALLENGE',
      title: 'Reading Challenge',
      description: 'Create a challenge based on reading time or books completed',
      icon: Book,
      color: 'bg-purple-500',
      placeholder: {
        title: 'Summer Reading Marathon',
        description: 'Challenge readers to complete books within a timeframe',
        targetGoal: 'Target reading hours (e.g., 20)',
      }
    },
    {
      type: 'BOOK_CLUB',
      title: 'Book Club',
      description: 'Create a reading group for specific books',
      icon: Users,
      color: 'bg-green-500',
      placeholder: {
        title: 'Fantasy Book Club',
        description: 'Join us in reading and discussing fantasy novels',
        targetGoal: 'Discussion topics count (e.g., 4)',
      }
    }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/v1/challenges', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.data.success) {
        toast.success('Challenge created successfully!');
        navigate('/book-forum');
      }
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create challenge');
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: colors.themeLight.color060d13,
        display: 'flex',
        flexDirection: 'column',
      }}>
      <HeaderComponent centerContent="" showSearch={false} />

      <div className="container mx-auto px-4 py-8 mt-12">
        <h1 className="text-4xl font-bold mb-8">Create Forum Challenge</h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          {challengeTypes.map((type) => (
            <div
              key={type.type}
              onClick={() => {
                setActiveTab(type.type);
                setFormData(prev => ({
                  ...prev,
                  type: type.type,
                  title: '',
                  description: '',
                  targetGoal: ''
                }));
              }}
              className={`cursor-pointer rounded-lg p-6 transition-all ${
                activeTab === type.type
                  ? `${type.color} text-white shadow-lg transform -translate-y-1`
                  : 'bg-white hover:shadow-md'
              }`}
            >
              <div className="flex items-center gap-4 mb-4">
                <type.icon className={`w-8 h-8 ${activeTab === type.type ? 'text-white' : type.color.replace('bg-', 'text-')}`} />
                <h3 className="text-xl font-semibold">{type.title}</h3>
              </div>
              <p className={activeTab === type.type ? 'text-white/90' : 'text-gray-600'}>
                {type.description}
              </p>
            </div>
          ))}
        </div>

        <Card className="p-8">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Challenge Title
                </label>
                <input
                  type="text"
                  placeholder={challengeTypes.find(t => t.type === activeTab).placeholder.title}
                  className="w-full p-3 border rounded-lg"
                  value={formData.title}
                  onChange={(e) => setFormData({...formData, title: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Target Goal
                </label>
                <input
                  type="number"
                  placeholder={challengeTypes.find(t => t.type === activeTab).placeholder.targetGoal}
                  className="w-full p-3 border rounded-lg"
                  value={formData.targetGoal}
                  onChange={(e) => setFormData({...formData, targetGoal: e.target.value})}
                  required
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                placeholder={challengeTypes.find(t => t.type === activeTab).placeholder.description}
                className="w-full p-3 border rounded-lg"
                rows={4}
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date
                </label>
                <input
                  type="datetime-local"
                  className="w-full p-3 border rounded-lg"
                  value={formData.startDate}
                  onChange={(e) => setFormData({...formData, startDate: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date
                </label>
                <input
                  type="datetime-local"
                  className="w-full p-3 border rounded-lg"
                  value={formData.endDate}
                  onChange={(e) => setFormData({...formData, endDate: e.target.value})}
                  required
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Reward
              </label>
              <input
                type="text"
                placeholder="e.g., Summer Reader Badge, Special Recognition"
                className="w-full p-3 border rounded-lg"
                value={formData.reward}
                onChange={(e) => setFormData({...formData, reward: e.target.value})}
                required
              />
            </div>

            <div className="flex justify-end gap-4">
              <button
                type="button"
                onClick={() => navigate('/book-forum')}
                className="px-6 py-2 text-gray-600 bg-gray-100 rounded-lg hover:bg-gray-200"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-6 py-2 text-white bg-blue-600 rounded-lg hover:bg-blue-700"
              >
                Create Challenge
              </button>
            </div>
          </form>
        </Card>
      </div>
    </Box>
  );
};

export default CreateForumChallenge;
