// BookForum.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import ForumPost from './widgets/ForumPost';

const BookForum = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '', bookId: null });
  const [loading, setLoading] = useState(false);
  const [showCreatePost, setShowCreatePost] = useState(false);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8080/api/v1/forum/posts', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setPosts(response.data);
    } catch (error) {
      console.error('Failed to fetch posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const createPost = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:8080/api/v1/forum/posts',
        newPost,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setPosts([response.data, ...posts]);
      setNewPost({ title: '', content: '', bookId: null });
      setShowCreatePost(false);
    } catch (error) {
      console.error('Failed to create post:', error);
    }
  };

  const handleLike = async (postId) => {
    try {
      await axios.post(
        `http://localhost:8080/api/v1/forum/posts/${postId}/like`,
        {},
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      fetchPosts(); // Refresh posts to show updated likes
    } catch (error) {
      console.error('Failed to like post:', error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4 mt-24">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Book Discussion Forum</h1>
        <Button
          onClick={() => setShowCreatePost(!showCreatePost)}
          className="bg-green-600 hover:bg-green-700"
        >
          {showCreatePost ? 'Cancel' : 'Create New Post'}
        </Button>
      </div>

      {showCreatePost && (
        <form onSubmit={createPost} className="mb-8 p-4 bg-white rounded-lg shadow">
          <Input
            type="text"
            placeholder="Post Title"
            value={newPost.title}
            onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
            className="mb-4"
          />
          <Textarea
            placeholder="Write your post..."
            value={newPost.content}
            onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
            className="mb-4"
            rows={4}
          />
          <Button type="submit" className="bg-green-600 hover:bg-green-700">
            Post
          </Button>
        </form>
      )}

      {loading ? (
        <div className="text-center">Loading posts...</div>
      ) : (
        <div className="space-y-4">
          {posts.map((post) => (
            <ForumPost
              key={post.id}
              post={post}
              onLike={handleLike}
              onComment={() => {/* Handle comment */}}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default BookForum;
