import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};
  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showNoteOption, setShowNoteOption] = useState(false);
  const [notePosition, setNotePosition] = useState({ x: 0, y: 0 });
  const [selectedText, setSelectedText] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [noteContent, setNoteContent] = useState('');
  const iframeRef = useRef(null);

  // Xử lý khi người dùng bôi đen text trong iframe
  const handleIframeLoad = () => {
    const iframe = iframeRef.current;
    if (!iframe) return;

    const iframeDocument = iframe.contentDocument || iframe.contentWindow.document;

    iframeDocument.addEventListener('mouseup', (event) => {
      const selection = iframeDocument.getSelection();
      const text = selection.toString().trim();

      if (text) {
        const range = selection.getRangeAt(0);
        const rect = range.getBoundingClientRect();
        const iframeRect = iframe.getBoundingClientRect();

        // Tính toán vị trí hiển thị option
        setNotePosition({
          x: rect.left + iframeRect.left + (rect.width / 2),
          y: rect.top + iframeRect.top - 40
        });
        setSelectedText(text);
        setShowNoteOption(true);
      } else {
        setShowNoteOption(false);
      }
    });
  };

  const handleCreateNote = () => {
    setOpenDialog(true);
  };

  const handleSaveNote = async () => {
    const selection = iframeRef.current.contentDocument.getSelection();
    const range = selection.getRangeAt(0);

    // Tạo một đối tượng note để lưu vào database
    const noteData = {
      bookId,
      content: noteContent,
      selectedText,
      // Lưu thông tin về vị trí của note
      location: {
        startContainer: getXPath(range.startContainer),
        startOffset: range.startOffset,
        endContainer: getXPath(range.endContainer),
        endOffset: range.endOffset
      }
    };

    try {
      const response = await fetch('http://localhost:8080/api/v1/notes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(noteData)
      });

      if (response.ok) {
        // Đóng dialog và reset state
        setOpenDialog(false);
        setNoteContent('');
        setShowNoteOption(false);
      }
    } catch (error) {
      console.error('Error saving note:', error);
    }
  };

  // Hàm tạo XPath cho một node
  const getXPath = (node) => {
    if (node.nodeType === Node.TEXT_NODE) {
      node = node.parentNode;
    }

    if (node.id) {
      return `//*[@id="${node.id}"]`;
    }

    let path = [];
    while (node && node.nodeType === Node.ELEMENT_NODE) {
      let position = 1;
      let sibling = node.previousSibling;

      while (sibling) {
        if (sibling.nodeType === Node.ELEMENT_NODE &&
            sibling.tagName === node.tagName) {
          position++;
        }
        sibling = sibling.previousSibling;
      }

      path.unshift(`${node.tagName.toLowerCase()}[${position}]`);
      node = node.parentNode;
    }

    return `/${path.join('/')}`;
  };

  // Load saved notes and highlight them
  const loadSavedNotes = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/notes/book/${bookId}`);
      const notes = await response.json();

      notes.forEach(note => {
        highlightSavedNote(note);
      });
    } catch (error) {
      console.error('Error loading notes:', error);
    }
  };

  // Highlight saved note in the document
  const highlightSavedNote = (note) => {
    const iframe = iframeRef.current;
    if (!iframe) return;

    const doc = iframe.contentDocument;
    const startNode = getElementByXPath(doc, note.location.startContainer);
    const endNode = getElementByXPath(doc, note.location.endContainer);

    if (startNode && endNode) {
      const range = doc.createRange();
      range.setStart(startNode, note.location.startOffset);
      range.setEnd(endNode, note.location.endOffset);

      const highlight = doc.createElement('span');
      highlight.className = 'highlighted-note';
      highlight.style.backgroundColor = 'yellow';
      highlight.title = note.content;

      range.surroundContents(highlight);
    }
  };

  // Helper function to get element by XPath
  const getElementByXPath = (doc, xpath) => {
    return doc.evaluate(xpath, doc, null,
      XPathResult.FIRST_ORDERED_NODE_TYPE, null).singleNodeValue;
  };

  useEffect(() => {
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  // Load saved notes when iframe loads
  useEffect(() => {
    if (iframeRef.current) {
      iframeRef.current.addEventListener('load', () => {
        handleIframeLoad();
        loadSavedNotes();
      });
    }
  }, [iframeRef.current]);


  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;

  return (
    <div>
      <div style={{ ...styles.header }}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      {showNoteOption && (
        <div style={{
          position: 'absolute',
          left: notePosition.x,
          top: notePosition.y,
          zIndex: 2000,
          backgroundColor: 'white',
          padding: '5px',
          borderRadius: '4px',
          boxShadow: '0 2px 5px rgba(0,0,0,0.2)'
        }}>
          <Button onClick={handleCreateNote}>
            Tạo ghi chú
          </Button>
        </div>
      )}

      {/* Note creation dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Tạo ghi chú mới</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nội dung ghi chú"
            fullWidth
            multiline
            rows={4}
            value={noteContent}
            onChange={(e) => setNoteContent(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
          <Button onClick={handleSaveNote}>Lưu</Button>
        </DialogActions>
      </Dialog>
      <iframe
        src={`http://localhost:8080/api/v1/book/book-content?url=${bookData.formats['text/html']}&urlImageCover=${bookData.formats['image/jpeg']}`}
        style={styles.bookContent}
        title="Book Content"
      />
      <div style={{
        position: 'fixed',
        bottom: 0,
        height: '9vh', width: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.0)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        }}>
      <span style={styles.chapterLabel}>Các Chương</span>
      <button
      onClick={()=>{}} style={styles.button}>
        <img src={images.icRightArrow}
          alt="Next" style={styles.icon} />
      </button>
      </div>
    </div>
  );
}

const styles = {
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '19%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '81%',
    marginLeft: '19%',
    height: '100vh',
    border: 'none',
  },
};

export default ReadBookScreen;
