import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import Note from './Note';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [notes, setNotes] = useState([]);
  const [content, setContent] = useState('');
  const [selectedText, setSelectedText] = useState('');
  const [showNoteBox, setShowNoteBox] = useState(false);

  useEffect(() => {
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then(response => response.json())
        .then(data => {
          setBookData(data);
          fetch(data.formats['text/html'])
            .then(res => res.text())
            .then(html => setContent(html))
            .catch(error => console.error('Error fetching content:', error));
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  const handleSaveNote = (note) => {
    setNotes(prevNotes => [...prevNotes, note]);
    setShowNoteBox(false);
  };

  const handleTextSelection = () => {
    const selection = window.getSelection();
    if (selection.toString().length > 0) {
      setSelectedText(selection.toString());
      setShowNoteBox(true);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors } = bookData;

  return (
    <div>
      <div style={{ ...styles.header }}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map(author => author.name).join(', ')}
        </p>
      </div>
      <div
        style={styles.bookContent}
        dangerouslySetInnerHTML={{ __html: content }}
        onMouseUp={handleTextSelection}
      />
      {showNoteBox && (
        <div style={styles.noteBox}>
          <textarea
            placeholder="Enter your note here..."
            rows={4}
            style={styles.textarea}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSaveNote({
                  text: selectedText,
                  note: e.target.value,
                });
                e.target.value = '';
              }
            }}
          />
        </div>
      )}
      <div style={styles.footer}>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icLeftArrow} alt="Back" style={styles.icon} />
        </button>
        <span style={styles.chapterLabel}>Chương 1</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
      <div style={styles.notesContainer}>
        <h2>Notes</h2>
        {notes.map((note, index) => (
          <div key={index} style={styles.note}>
            <strong>Selected Text:</strong> {note.text}<br />
            <strong>Note:</strong> {note.note}
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '19%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '100%',
    height: 'calc(100vh - 60px)', // Adjust for header and footer
    overflowY: 'scroll',
    padding: '10px',
  },
  footer: {
    position: 'fixed',
    bottom: 0,
    height: '9vh',
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.0)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  notesContainer: {
    position: 'fixed',
    bottom: '50px',
    right: '10px',
    backgroundColor: 'white',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '5px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
    maxHeight: '200px',
    overflowY: 'auto',
  },
  note: {
    padding: '5px',
    borderBottom: '1px solid #ddd',
  },
  noteBox: {
    position: 'fixed',
    bottom: '10px',
    right: '10px',
    backgroundColor: 'white',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '5px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
  },
  textarea: {
    width: '300px',
    height: '100px',
  },
};

export default ReadBookScreen;
