import React, { useEffect, useState, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import { ReactReader } from 'react-reader';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [state, setState] = useState({
    loading: true,
    error: null,
    bookData: null,
    epubBlob: null,
  });
  const [readerLocation, setReaderLocation] = useState(null);
  const [notes, setNotes] = useState([]);

  const fetchBookData = useCallback(async () => {
    if (!bookId) return;

    try {
      setState(prev => ({ ...prev, loading: true, error: null }));

      const bookResponse = await fetch(`https://gutendex.com/books/${bookId}`);
      if (!bookResponse.ok) throw new Error('Failed to fetch book data');
      const bookData = await bookResponse.json();
      console.log('Book data:', bookData);

      const epubUrl = bookData.formats['application/epub+zip'];
      if (!epubUrl) throw new Error('EPUB format not available');

      const epubResponse = await fetch(epubUrl);
      if (!epubResponse.ok) throw new Error('Failed to fetch EPUB');
      const epubBlob = await epubResponse.blob();
      console.log('EPUB blob loaded, size:', epubBlob.size);

      setState({
        loading: false,
        error: null,
        bookData,
        epubBlob,
      });
    } catch (error) {
      console.error('Error:', error.message);
      setState(prev => ({ ...prev, loading: false, error: error.message }));
    }
  }, [bookId]);

  useEffect(() => {
    fetchBookData();
  }, [fetchBookData]);

  const locationChanged = (epubcifi) => {
    setReaderLocation(epubcifi);
  };

  const addNote = (content) => {
    const newNote = {
      bookId,
      content,
      location: readerLocation
    };
    setNotes(prev => [...prev, newNote]);
  };

  if (state.loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (state.error) {
    return <div>Error: {state.error}</div>;
  }

  if (!state.bookData || !state.epubBlob) {
    return <div>No book data found or EPUB not loaded</div>;
  }

  const { title, authors } = state.bookData;

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <div style={styles.readerContainer}>
        <ReactReader
          url={URL.createObjectURL(state.epubBlob)}
          location={readerLocation}
          locationChanged={locationChanged}
          epubOptions={{
            flow: "scrolled",
            manager: "continuous"
          }}
        />
      </div>
      <div style={styles.footer}>
        <span style={styles.chapterLabel}>Các Chương</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
      <div style={styles.noteContainer}>
        <input
          type="text"
          placeholder="Add a note..."
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              addNote(e.target.value);
              e.target.value = '';
            }
          }}
        />
        {notes.map((note, index) => (
          <div key={index}>{note.content}</div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
  },
  header: {
    padding: '10px',
    borderBottom: '1px solid #ddd',
  },
  readerContainer: {
    flex: 1,
    overflow: 'hidden',
  },
  footer: {
    position: 'fixed',
    bottom: 0,
    height: '9vh',
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  noteContainer: {
    position: 'fixed',
    right: 0,
    top: '50%',
    transform: 'translateY(-50%)',
    width: '200px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    padding: '10px',
    borderLeft: '1px solid #ddd',
  },
};

export default ReadBookScreen;
