import React, { useState } from 'react';
import { Flag, Check, X, AlertCircle, Clock, Ban, Bell } from 'lucide-react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField } from '@mui/material';
import { toast } from 'react-toastify';
import { format } from 'date-fns';

const ActionMenu = ({ onSelect, reportId }) => {
  const [showBanDialog, setShowBanDialog] = useState(false);
  const [selectedAction, setSelectedAction] = useState(null);
  const [banReason, setBanReason] = useState('');

  const actions = [
    { id: 'dismiss', label: 'Dismiss Report', icon: <X className="w-4 h-4" /> },
    { id: 'warn', label: 'Warn User', icon: <AlertCircle className="w-4 h-4" /> },
    { id: 'ban_1h', label: 'Ban Forum Creation (1h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'ban_3h', label: 'Ban Forum Creation (3h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'ban_24h', label: 'Ban Forum Creation (24h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'ban_permanent', label: 'Permanent Ban', icon: <Ban className="w-4 h-4" /> },
  ];

  const handleActionClick = (actionId) => {
    if (actionId.includes('ban')) {
      setSelectedAction(actionId);
      setShowBanDialog(true);
    } else {
      handleAction(actionId);
    }
  };

  const handleAction = async (actionId, reason = '') => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/admin/forum-reports/${reportId}/action`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          action: actionId.toUpperCase(),
          reason: reason
        })
      });

      if (response.ok) {
        toast.success('Action applied successfully');
        onSelect(actionId);
      } else {
        throw new Error('Failed to apply action');
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  const handleBanConfirm = () => {
    if (!banReason.trim()) {
      toast.error('Please provide a reason for the ban');
      return;
    }
    handleAction(selectedAction, banReason);
    setShowBanDialog(false);
    setBanReason('');
  };

  return (
    <>
      <div className="absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5">
        <div className="py-1" role="menu">
          {actions.map((action) => (
            <button
              key={action.id}
              onClick={() => handleActionClick(action.id)}
              className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
              role="menuitem"
            >
              {action.icon}
              <span className="ml-2">{action.label}</span>
            </button>
          ))}
        </div>
      </div>

      <Dialog open={showBanDialog} onOpenChange={setShowBanDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Ban User</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 p-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Reason for ban (required)</label>
              <textarea
                value={banReason}
                onChange={(e) => setBanReason(e.target.value)}
                className="w-full p-2 border rounded-md"
                rows={4}
                placeholder="Enter reason for ban..."
              />
            </div>
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => {
                  setShowBanDialog(false);
                  setBanReason('');
                }}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleBanConfirm}
                disabled={!banReason.trim()}
                className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700 disabled:opacity-50"
              >
                Confirm Ban
              </button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
};

export default ActionMenu;
