import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import { color } from '@mui/system';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);

  const [chapters, setChapters] = useState([]);
  const [showChapters, setShowChapters] = useState(false);

  useEffect(() => {
    console.log('bookId:', bookId);
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
          if (data.formats['text/html']) {
            fetchChapters(data.formats['text/html']);
          }
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  const fetchChapters = (url) => {
    fetch(`http://localhost:8080/api/v1/book/chapters?url=${url}`)
      .then((response) => response.json())
      .then((chapterData) => {
        console.log('chapterData:', chapterData);
        // Gọi hàm để bôi đen văn bản cụ thể
        const highlightedContent = highlightText(chapterData);
        // Đặt nội dung đã sửa đổi vào iframe
        const iframe = document.getElementById("your-iframe-id");
        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        iframeDoc.write(highlightedContent);
        iframeDoc.close();
      })
      .catch((error) => {
        console.error('Error fetching chapters:', error);
      });
  };

  // Hàm để bôi đen văn bản cụ thể
  const highlightText = (content) => {
    const textToHighlight = "Shall you wear them in company?";
    const highlightedText = `<span style="background-color: yellow;">${textToHighlight}</span>`;
    return content.replace(new RegExp(textToHighlight, 'g'), highlightedText);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;
  return (
    <div>
      <div style={{ ...styles.header }}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
        <div>
          <h1>Book Cover</h1>
            <img
              src={formats['image/jpeg']}
        alt="Book Cover"
        style={{
          marginTop: '10px',
                width: '70px', height: 'auto' }}
            />
        </div>
      </div>

      <div style={{
        position: 'fixed',
        top: 0,
        right: 0,
        width: '19%',
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        padding: '10px',
        borderBottom: '1px solid #ddd',
        zIndex: 1000,
        transition: 'opacity 0.3s ease',
      }}>
        <h1>Note Book</h1>
      </div>
      <iframe
        id="your-iframe-id"
        src={ formats['text/html']||formats['text/plain; charset=us-ascii'] }
        style={styles.bookContent}
        title="Book Content"
      />
      <div style={{
        position: 'fixed',
        bottom: 0,
        height: '9vh', width: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.0)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        }}>
          <div style={{
            width: '15%',
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
          onClick={() => {
            setShowChapters(!showChapters)}}
          >
            <span style={styles.chapterLabel}  >Các chương</span>
      <button
      onClick={()=>{}} style={styles.button}>
        <img src={images.icListChapter}
          alt="list-chapter" style={styles.icon} />
      </button>
          </div>

      </div>
      {showChapters && (
        <div style={styles.chapterDropdown}>
          <ul>
            {chapters.map((chapter, index) => (
              <li key={index}
              >{chapter.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

const styles = {
  chapterDropdown: {
    position: 'absolute',
    top: '40px',
    left: '50%',
    transform: 'translateX(-50%)',
    width: '18%',
    backgroundColor: '#fff',
    border: '1px solid #ddd',
    borderRadius: '4px',
    zIndex: 1000,
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    padding: '10px',
    maxHeight: '85%',
    overflowY: 'auto',
  },
  icon: {
    width: '20px',
    height: '20px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#000000',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '19%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '100%',
    height: '100vh',
    border: 'none',
  },
};

export default ReadBookScreen;
