import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { ReactReader } from 'react-reader';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;
  const epubUrl = `https://www.gutenberg.org/cache/epub/${bookId}/pg${bookId}.epub`;

    console.log('epubUrl', epubUrl);
  return (
    <div style={{ height: '100vh', position: 'relative' }}>
      <div style={styles.header}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <div style={styles.readerContainer}>
        <ReactReader
          url={epubUrl}
          title={title}
          location={'epubcfi(/6/2[cover]!/6)'}
          showToc={true}
        />
      </div>
      <div style={styles.footer}>
        <span style={styles.chapterLabel}>Các Chương</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
    </div>
  );
}

const styles = {
  header: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    zIndex: 1000,
  },
  readerContainer: {
    height: 'calc(100vh - 120px)',
    marginTop: '60px',
  },
  footer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    height: '60px',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
    marginRight: '10px',
  },
};

export default ReadBookScreen;
