import React, { useState } from 'react';
import { Flag, Check, X, AlertCircle, Clock, Ban, Trash2, MoreVertical } from 'lucide-react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Checkbox, FormControlLabel, FormGroup, Alert, IconButton, Menu, MenuItem } from '@mui/material';
import { toast } from 'react-toastify';

const ActionMenu = ({ reportId, forumId }) => {
  const [showBanDialog, setShowBanDialog] = useState(false);
  const [selectedAction, setSelectedAction] = useState(null);
  const [banReason, setBanReason] = useState('');
  const [banTypes, setBanTypes] = useState({
    noInteraction: false,
    noComment: false,
    noJoin: false,
    noForumCreation: false,
    deleteForum: false
  });
  const [anchorEl, setAnchorEl] = useState(null);

  const actions = [
    { id: 'DISMISS', label: 'Dismiss Report', icon: <X className="w-4 h-4" /> },
    { id: 'WARN', label: 'Warn User', icon: <AlertCircle className="w-4 h-4" /> },
    { id: 'BAN_1H', label: 'Ban User (1h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'BAN_3H', label: 'Ban User (3h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'BAN_24H', label: 'Ban User (24h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'BAN_PERMANENT', label: 'Permanent Ban', icon: <Ban className="w-4 h-4" /> },
  ];

  const handleActionClick = (actionId) => {
    if (actionId === 'DISMISS') {
      handleAction(actionId);
    } else {
      setSelectedAction(actionId);
      setBanTypes({
        noInteraction: false,
        noComment: false,
        noJoin: false,
        noForumCreation: false,
        deleteForum: false
      });
      setShowBanDialog(true);
      setAnchorEl(null);
    }
  };

  const handleBanTypeChange = (event) => {
    setBanTypes({
      ...banTypes,
      [event.target.name]: event.target.checked
    });
  };

  const handleReasonSubmit = async () => {
    if (!banReason.trim() && selectedAction !== 'DISMISS') {
      toast.error('Please provide a reason');
      return;
    }

    if (selectedAction?.includes('BAN') && !Object.values({ ...banTypes, deleteForum: false }).some(value => value)) {
      toast.error('Please select at least one ban type');
      return;
    }

    try {
      await handleAction(selectedAction, banReason);

      setShowBanDialog(false);
      setBanReason('');
      setBanTypes({
        noInteraction: false,
        noComment: false,
        noJoin: false,
        noForumCreation: false,
        deleteForum: false
      });

      window.location.reload();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.message);
    }
  };

  const handleAction = async (actionId, reason = '') => {
    try {
      const body = {
        action: actionId,
        reason: reason || null,
        banTypes: actionId.startsWith('BAN_') ? banTypes : null
      };

      const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/forums/reports/${reportId}/action`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(body)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to apply action');
      }

      await response.json();

      if (banTypes.deleteForum) {
        await new Promise(resolve => setTimeout(resolve, 500));
        await handleDeleteForum();
      }

      toast.success('Action applied successfully');

      setTimeout(() => {
        window.location.reload();
      }, 1000);

    } catch (error) {
      console.error('Action error:', error);
      toast.error(error.message);
      throw error;
    }
  };

  const handleDeleteForum = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/forums/${forumId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete forum');
      }
    } catch (error) {
      throw new Error('Failed to delete forum: ' + error.message);
    }
  };

  return (
    <>
      <IconButton onClick={(event) => setAnchorEl(event.currentTarget)}>
        <MoreVertical className="w-5 h-5" />
      </IconButton>

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={() => setAnchorEl(null)}
      >
        {actions.map((action) => (
          <MenuItem
            key={action.id}
            onClick={() => handleActionClick(action.id)}
            className="flex items-center gap-2"
          >
            {action.icon}
            <span>{action.label}</span>
          </MenuItem>
        ))}
      </Menu>

      <Dialog
        open={showBanDialog}
        onClose={() => setShowBanDialog(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {selectedAction?.includes('BAN') ? 'Ban User' : 'Warning User'}
        </DialogTitle>
        <DialogContent>
          {selectedAction?.includes('BAN') && (
            <>
              <Alert severity="info" className="mb-4 mt-4">
                Please select at least one restriction type
              </Alert>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={banTypes.noInteraction}
                      onChange={handleBanTypeChange}
                      name="noInteraction"
                    />
                  }
                  label="Ban user from forum interactions (like, save)"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={banTypes.noComment}
                      onChange={handleBanTypeChange}
                      name="noComment"
                    />
                  }
                  label="Ban user from commenting"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={banTypes.noJoin}
                      onChange={handleBanTypeChange}
                      name="noJoin"
                    />
                  }
                  label="Ban user from joining forums"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={banTypes.noForumCreation}
                      onChange={handleBanTypeChange}
                      name="noForumCreation"
                    />
                  }
                  label="Ban user from creating forums"
                />
                {(selectedAction?.includes('BAN_') && Object.values({ ...banTypes, deleteForum: false }).some(value => value)) && (
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={banTypes.deleteForum}
                        onChange={handleBanTypeChange}
                        name="deleteForum"
                      />
                    }
                    label="Delete this forum"
                  />
                )}
              </FormGroup>
            </>
          )}
          <TextField
            margin="dense"
            label={selectedAction?.includes('BAN') ? "Reason for ban" : "Warning message"}
            fullWidth
            multiline
            rows={4}
            value={banReason}
            onChange={(e) => setBanReason(e.target.value)}
            variant="outlined"
            required
          />
        </DialogContent>
        <DialogActions sx={{ p: 2 }}>
          <Button
            onClick={() => {
              setShowBanDialog(false);
              setBanReason('');
              setBanTypes({
                noInteraction: false,
                noComment: false,
                noJoin: false,
                noForumCreation: false,
                deleteForum: false
              });
            }}
            color="inherit"
          >
            Cancel
          </Button>
          <Button
            onClick={handleReasonSubmit}
            disabled={!banReason.trim() || (selectedAction?.includes('BAN') && !Object.values({ ...banTypes, deleteForum: false }).some(value => value))}
            variant="contained"
            color="error"
          >
            {selectedAction?.includes('BAN') ? 'Confirm Ban' : 'Send Warning'}
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default ActionMenu;
