// ForumPost.js
import React from 'react';
import { Avatar, Card, CardHeader, CardContent, CardActions, Button, Typography } from '@/components/ui/card';
import { formatDistanceToNow } from 'date-fns';

const ForumPost = ({ post, onLike, onComment, user }) => {
  return (
    <Card className="mb-4">
      <CardHeader>
        <div className="flex items-center space-x-4">
          <Avatar>
            {post.author.avatar ? (
              <img src={post.author.avatar} alt={post.author.username} />
            ) : (
              post.author.username.charAt(0)
            )}
          </Avatar>
          <div>
            <Typography className="font-semibold">{post.author.username}</Typography>
            <Typography className="text-sm text-gray-500">
              {formatDistanceToNow(new Date(post.createdAt), { addSuffix: true })}
            </Typography>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <Typography className="text-lg font-semibold mb-2">{post.title}</Typography>
        <Typography>{post.content}</Typography>
        {post.bookInfo && (
          <div className="mt-4 p-4 bg-gray-100 rounded-lg">
            <div className="flex items-start space-x-4">
              <img src={post.bookInfo.cover} alt={post.bookInfo.title} className="w-24 h-32 object-cover" />
              <div>
                <Typography className="font-semibold">{post.bookInfo.title}</Typography>
                <Typography className="text-sm text-gray-600">by {post.bookInfo.author}</Typography>
              </div>
            </div>
          </div>
        )}
      </CardContent>
      <CardActions>
        <Button
          variant="ghost"
          onClick={() => onLike(post.id)}
          className="flex items-center space-x-2"
        >
          <span>üëç</span>
          <span>{post.likes} Likes</span>
        </Button>
        <Button
          variant="ghost"
          onClick={() => onComment(post.id)}
          className="flex items-center space-x-2"
        >
          <span>üí¨</span>
          <span>{post.comments.length} Comments</span>
        </Button>
      </CardActions>
    </Card>
  );
};

export default ForumPost;
