import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import ReactHtmlParser from 'react-html-parser';  // Import thư viện phân tích HTML
import { images } from '../../../constants';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log('bookId:', bookId);
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;

  // Lấy nội dung sách từ định dạng HTML
  const bookContent = formats['text/html'] || formats['text/plain'] || '';

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <div style={styles.bookContent}>
        {/* Hiển thị nội dung sách */}
        {ReactHtmlParser(bookContent)}
      </div>
      <div style={styles.footer}>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icLeftArrow} alt="Back" style={styles.icon} />
        </button>
        <span style={styles.chapterLabel}>Chương 1</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    overflow: 'auto',
    padding: '0 10px',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '100%',
    maxWidth: '800px',  // Điều chỉnh kích thước tối đa
    margin: '20px auto',
    padding: '20px',
    boxSizing: 'border-box',
    textAlign: 'justify',
  },
  footer: {
    position: 'fixed',
    bottom: 0,
    height: '9vh',
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.0)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

export default ReadBookScreen;
