// ForumPost.js
import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Avatar,
  Typography,
  Button,
  Box,
  Paper
} from '@mui/material';
import { ThumbUp, Comment } from '@mui/icons-material';
import { formatDistanceToNow } from 'date-fns';

const ForumPost = ({ post, onLike, onComment }) => {
  return (
    <Card sx={{ mb: 2 }}>
      <CardHeader
        avatar={
          <Avatar src={post.author.avatar}>
            {post.author.username ? post.author.username.charAt(0) : 'U'}
          </Avatar>
        }
        title={post.author.username}
        subheader={formatDistanceToNow(new Date(post.createdAt), { addSuffix: true })}
      />
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {post.title}
        </Typography>
        <Typography variant="body1" paragraph>
          {post.content}
        </Typography>
        {post.bookInfo && (
          <Paper
            elevation={1}
            sx={{
              p: 2,
              mt: 2,
              bgcolor: 'rgba(0, 0, 0, 0.02)'
            }}
          >
            <Box sx={{ display: 'flex', gap: 2 }}>
              <img
                src={post.bookInfo.cover}
                alt={post.bookInfo.title}
                style={{
                  width: '100px',
                  height: '140px',
                  objectFit: 'cover'
                }}
              />
              <Box>
                <Typography variant="subtitle1" fontWeight="bold">
                  {post.bookInfo.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  by {post.bookInfo.author}
                </Typography>
              </Box>
            </Box>
          </Paper>
        )}
      </CardContent>
      <CardActions>
        <Button
          startIcon={<ThumbUp />}
          onClick={() => onLike(post.id)}
          size="small"
        >
          {post.likes} Likes
        </Button>
        <Button
          startIcon={<Comment />}
          onClick={() => onComment(post.id)}
          size="small"
        >
          {post.comments?.length || 0} Comments
        </Button>
      </CardActions>
    </Card>
  );
};

export default ForumPost;
