import React, {useState, useRef, useEffect} from 'react'
import {ListBook} from '../../pages'
import {Search, SlidersHorizontal, X, Check} from 'lucide-react'
import HeaderComponent from '../../component/header/HeaderComponent'
import {languages, subjects} from '../../constants/searchData'
import { useLocation, useNavigate } from 'react-router-dom'
import { SEARCH_MODE } from '../../constants/enums'

const MultiSelect = ({value, onChange, options, placeholder}) => {
  const [isOpen, setIsOpen] = useState(false)
  const [searchTerm, setSearchTerm] = useState('')
  const [selectedBooks, setSelectedBooks] = useState([]);
  const location = useLocation();
  const navigate = useNavigate();
  const wrapperRef = useRef(null)

  useEffect(() => {
    function handleClickOutside(event) {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setIsOpen(false)
      }
    }

    document.addEventListener('mousedown', handleClickOutside)
    return () => {
      document.removeEventListener('mousedown', handleClickOutside)
    }
  }, [wrapperRef])

  const filteredOptions = options.filter(option =>
    option.label.toLowerCase().includes(searchTerm.toLowerCase()),
  )

  const selectedValues = Array.isArray(value) ? value : []

  return (
    <div className='relative' ref={wrapperRef}>
      <div
        className='w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent cursor-pointer flex justify-between items-center'
        onClick={() => setIsOpen(!isOpen)}>
        <div className='flex flex-wrap gap-2'>
          {selectedValues.map(selectedValue => {
            const selectedOption = options.find(
              option => option.value === selectedValue,
            )
            return (
              <div
                key={selectedValue}
                className='px-2 py-1 bg-blue-500 text-white rounded-full flex items-center'>
                <span>{selectedOption.label}</span>
                <button
                  type='button'
                  className='ml-2 focus:outline-none'
                  onClick={e => {
                    e.stopPropagation()
                    onChange(selectedValues.filter(v => v !== selectedValue))
                  }}>
                  <X className='w-4 h-4 text-white' />
                </button>
              </div>
            )
          })}
          {!selectedValues.length && (
            <span className='text-gray-400'>{placeholder}</span>
          )}
        </div>
        <span className='transform transition-transform duration-200'>
          {isOpen ? '▲' : '▼'}
        </span>
      </div>

      {isOpen && (
        <div
          className={`absolute z-50 mt-1 w-full bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-hidden ${
            wrapperRef.current &&
            wrapperRef.current.getBoundingClientRect().bottom + 240 >
              window.innerHeight
              ? 'bottom-full mb-1'
              : 'top-full'
          }`}>
          <div className='sticky top-0 bg-white px-4 py-2'>
            <div className='relative'>
              <input
                type='text'
                placeholder='Search...'
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                className='w-full px-4 py-2 pr-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent'
              />
              {searchTerm && (
                <button
                  type='button'
                  className='absolute right-2 top-1/2 transform -translate-y-1/2 focus:outline-none'
                  onClick={() => setSearchTerm('')}>
                  <X className='w-4 h-4 text-gray-400' />
                </button>
              )}
            </div>
          </div>
          <div className='overflow-auto max-h-48'>
            {filteredOptions.map(option => (
              <div
                key={option.value}
                className='flex items-center px-4 py-2 hover:bg-gray-100 cursor-pointer'
                onClick={() => {
                  onChange(
                    selectedValues.includes(option.value)
                      ? selectedValues.filter(v => v !== option.value)
                      : [...selectedValues, option.value],
                  )
                }}>
                <div className='w-6 h-6 flex items-center justify-center mr-2'>
                  <input
                    type='checkbox'
                    checked={selectedValues.includes(option.value)}
                    readOnly
                    className='form-checkbox h-4 w-4 text-blue-500 transition duration-150 ease-in-out'
                  />
                </div>
                <span className='ml-2'>{option.label}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

const SearchResult = () => {
  const [searchParams, setSearchParams] = useState({
    title: '',
    author: '',
    subjects: [], // Changed to array for multiple selection
    language: '',
  })

  const [showResults, setShowResults] = useState(false)
  const [finalSearchTerm, setFinalSearchTerm] = useState('')
  const [isFilterExpanded, setIsFilterExpanded] = useState(true)
  const windowSize = {
    width: window.innerWidth,
    height: window.innerHeight,
  }
  const location = useLocation();
  const navigate = useNavigate();

  const mode = location.state?.mode || SEARCH_MODE.ADVANCED_SEARCH;

  const handleBookSelect = (book) => {
    if (mode === SEARCH_MODE.SELECT_BOOK) {
      navigate('/create-forum', {
        state: {
          bookId: book.id,
          bookTitle: book.title,
          authors: book.authors,
          subjects: book.subjects
        }
      });
    }
  };

  const handleSearch = e => {
    e.preventDefault();
    const searchTerms = [];

    // Xử lý title
    if (searchParams.title.trim()) {
      searchTerms.push(searchParams.title.trim());
    }

    // Xử lý author
    if (searchParams.author.trim()) {
      searchTerms.push(searchParams.author.trim());
    }

    // Tạo query string cho API
    let query = searchTerms.join(' ');

    // Thêm topics nếu có
    if (searchParams.subjects.length > 0) {
      query += `&topic=${searchParams.subjects.join(',')}`;
    }

    // Thêm language nếu có
    if (searchParams.language.length > 0) {
      query += `&languages=${searchParams.language}`;
    }

    if (query) {
      setFinalSearchTerm(query);
      setShowResults(true);
    }
  };
  const clearSearch = () => {
    setSearchParams({
      title: '',
      author: '',
      subjects: [],
      language: '',
    })
    setFinalSearchTerm('')
    setShowResults(false)
  }

  return (
    <div className='min-h-screen bg-gray-50'>
      <HeaderComponent
        windowSize={windowSize}
        centerContent={''}
        showSearch={false}
      />

      <div className='pt-20 px-4'>
        <div className='max-w-7xl mx-auto'>
          <div className='bg-white rounded-xl shadow-lg mb-8 overflow-visible'>
            <div className='p-6'>
              <div className='flex items-center justify-between mb-6'>
                <h2 className='text-2xl font-bold text-gray-800'>
                {mode === SEARCH_MODE.SELECT_BOOK ? 'Select Book For Create Forum' : 'Advanced Search'}
                </h2>
                <button
                  onClick={() => setIsFilterExpanded(!isFilterExpanded)}
                  className='p-2 hover:bg-gray-100 rounded-full transition-colors'>
                  <SlidersHorizontal className='w-5 h-5 text-gray-600' />
                </button>
              </div>

              <form
                onSubmit={handleSearch}
                className={`${isFilterExpanded ? 'block' : 'hidden'}`}>
                <div className='grid grid-cols-1 md:grid-cols-2 gap-6'>
                  {/* Title */}
                  <div className='col-span-full'>
                    <label className='block text-sm font-medium text-gray-700 mb-1'>
                      Title
                    </label>
                    <div className='relative'>
                      <input
                        type='text'
                        value={searchParams.title}
                        onChange={e =>
                          setSearchParams({
                            ...searchParams,
                            title: e.target.value,
                          })
                        }
                        placeholder='Search by book title...'
                        className='w-full pl-10 pr-10 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent'
                      />
                      <Search className='absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400' />
                      {searchParams.title && (
                        <button
                          type='button'
                          onClick={() =>
                            setSearchParams({...searchParams, title: ''})
                          }
                          className='absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600'>
                          <X className='w-5 h-5' />
                        </button>
                      )}
                    </div>
                  </div>

                  {/* Author */}
                  <div>
                    <label className='block text-sm font-medium text-gray-700 mb-1'>
                      Author
                    </label>
                    <input
                      type='text'
                      value={searchParams.author}
                      onChange={e =>
                        setSearchParams({
                          ...searchParams,
                          author: e.target.value,
                        })
                      }
                      placeholder='Author name...'
                      className='w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent'
                    />
                  </div>

                  {/* Language */}
                  <div>
                    <label className='block text-sm font-medium text-gray-700 mb-1'>
                      Language
                    </label>
                    <MultiSelect
                      value={searchParams.language}
                      onChange={newLanguage =>
                        setSearchParams({
                          ...searchParams,
                          language: newLanguage,
                        })
                      }
                      options={languages}
                      placeholder='Any language'
                    />
                  </div>

                  {/* Subject with Multiple Select */}
                  <div className='md:col-span-2'>
                    <label className='block text-sm font-medium text-gray-700 mb-1'>
                      Subjects
                    </label>
                    <MultiSelect
                      value={searchParams.subjects}
                      onChange={newSubjects =>
                        setSearchParams({
                          ...searchParams,
                          subjects: newSubjects,
                        })
                      }
                      options={subjects}
                      placeholder='Any subject'
                    />
                  </div>
                </div>

                {/* Action Buttons */}
                <div className='mt-8 flex justify-end space-x-4'>
                  <button
                    type='button'
                    onClick={clearSearch}
                    className='px-6 py-2.5 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors'>
                    Clear
                  </button>
                  <button
                    type='submit'
                    className='px-6 py-2.5 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors'>
                    Search Books
                  </button>
                </div>
              </form>
            </div>
          </div>

          {/* Results Section */}
          {showResults && finalSearchTerm && (
            <div className='mt-6'>
              <ListBook
              searchTerm={finalSearchTerm}
              windowSize={windowSize}
              mode={mode}
              onBookSelect={handleBookSelect}
              />
            </div>
          )}

          {/* No Search Yet Message */}
          {!showResults && (
            <div className='text-center py-16'>
              <h3 className='text-xl text-gray-600'>
                Use the filters above to search for books
              </h3>
              <p className='mt-2 text-gray-500'>
                You can search by title, author, subject, or language
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default SearchResult
