import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import { ReactReader } from 'react-reader';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [readerLocation, setReaderLocation] = useState(null);
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    console.log('bookId:', bookId);
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  const locationChanged = (epubcifi) => {
    setReaderLocation(epubcifi);
  };

  const addNote = (content) => {
    const newNote = {
      bookId,
      content,
      location: readerLocation
    };
    // Call your API to save the note here
    setNotes([...notes, newNote]);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;

  const epubUrl = formats['application/epub+zip'];
  const adjustedEpubUrl = epubUrl ? epubUrl.replace('https://www.gutenberg.org/ebooks/', 'https://www.gutenberg.org/cache/epub/') : null;

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <div style={styles.readerContainer}>
      {epubUrl ? (
          <ReactReader
            url={adjustedEpubUrl}
            location={readerLocation}
            locationChanged={locationChanged}
            epubOptions={{
              flow: "scrolled",
              manager: "continuous"
            }}
          />
        ) : (
          <div>EPUB format not available for this book.</div>
        )}
      </div>
      <div style={styles.footer}>
        <span style={styles.chapterLabel}>Các Chương</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
      <div style={styles.noteContainer}>
        <input
          type="text"
          placeholder="Add a note..."
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              addNote(e.target.value);
              e.target.value = '';
            }
          }}
        />
        {notes.map((note, index) => (
          <div key={index}>{note.content}</div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
  },
  header: {
    padding: '10px',
    borderBottom: '1px solid #ddd',
  },
  readerContainer: {
    flex: 1,
    overflow: 'hidden',
  },
  footer: {
    position: 'fixed',
    bottom: 0,
    height: '9vh',
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  noteContainer: {
    position: 'fixed',
    right: 0,
    top: '50%',
    transform: 'translateY(-50%)',
    width: '200px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    padding: '10px',
    borderLeft: '1px solid #ddd',
  },
};

export default ReadBookScreen;
