// Main SavedBooks Component
function SavedBooks() {
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [savedBooks, setSavedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const [currentTab, setCurrentTab] = useState(0);
  const [recentBooks, setRecentBooks] = useState([]);
  const navigate = useNavigate();

  const handleChangeTab = (event, newValue) => {
    setCurrentTab(newValue);
  };

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const getUser = async () => {
      if (!localStorage.getItem("token")) {
        navigate("/login-account");
        return;
      }
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_BASE_URL}/user/profile`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        setUser(response.data);
        fetchSavedBooks(response.data.userId);
      } catch (error) {
        console.error(error);
        setError("Failed to get user profile");
        setLoading(false);
      }
    };
    getUser();
  }, [navigate]);

  const fetchSavedBooks = async (userId) => {
    try {
      const savedResponse = await axios.get(
        `${process.env.REACT_APP_API_BASE_URL}/saved-books/user/${userId}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      if (savedResponse.data.success) {
        const bookPromises = savedResponse.data.data.slice(0, 20).map(Number);
        const books = await axios.post(
          `${process.env.REACT_APP_API_BASE_URL}/book/batch`,
          bookPromises
        );
        setSavedBooks(books.data.results);
      }
      setLoading(false);
    } catch (error) {
      console.error(error);
      setError("Failed to fetch saved books");
      setLoading(false);
    }
  };

  const handleUnsaveBook = async (bookId) => {
    try {
      const response = await axios.delete(
        `${process.env.REACT_APP_API_BASE_URL}/saved-books/user/${user.userId}/book/${bookId}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      if (response.data.success) {
        setSavedBooks((prev) => prev.filter((book) => book.id !== bookId));
      }
    } catch (error) {
      console.error("Error unsaving book:", error);
    }
  };

  if (loading) {
    return (
      <Box
        sx={{
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          bgcolor: colors.themeLight.color060d13,
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box
      sx={{
        height: "100vh",
        bgcolor: colors.themeLight.color060d13,
        flex: 1,
        flexDirection: "column",
        overflowY: "auto",
      }}
    >
      <HeaderComponent
        windowSize={windowSize}
        centerContent={"MY LIBRARY"}
        showSearch={false}
      />

      <Container
        sx={{
          pt: 12,
          pb: 6,
          flex: 1,
          overflowY: "auto",
          width: windowSize.width,
        }}
      >
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Tabs
          value={currentTab}
          onChange={handleChangeTab}
          sx={{
            mb: 4,
            "& .MuiTab-root": {
              color: "gray",
              "&.Mui-selected": {
                color: "#2196f3",
              },
            },
            "& .MuiTabs-indicator": {
              backgroundColor: "#2196f3",
            },
            borderBottom: 1,
            borderColor: "divider",
          }}
        >
          <Tab
            icon={<BookmarkPlus size={20} />}
            iconPosition="start"
            label="Saved Books"
          />
          <Tab
            icon={<History size={20} />}
            iconPosition="start"
            label="Recently Read"
          />
          <Tab
            icon={<ThumbsUp size={20} />}
            iconPosition="start"
            label="Recommended"
          />
        </Tabs>

        {currentTab === 0 &&
          (savedBooks.length === 0 ? (
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                height: "50vh",
                gap: 2,
              }}
            >
              <Typography variant="h6" color="white">
                No saved books yet
              </Typography>
              <Button variant="contained" onClick={() => navigate("/")}>
                Explore Books
              </Button>
            </Box>
          ) : (
            <Grid container spacing={3}>
              {savedBooks.map((book) => (
                <Grid item xs={12} sm={6} md={3} key={book.id}>
                  <BookCard
                    book={book}
                    onUnsave={handleUnsaveBook}
                    onClick={() =>
                      navigate("/description-book", {
                        state: { bookId: book.id, bookTitle: book.title },
                      })
                    }
                  />
                </Grid>
              ))}
            </Grid>
          ))}

        {currentTab === 1 && (
          <RecentlyReadBooks userId={user?.userId} maxBooks={20} />
        )}

        {currentTab === 2 && (
          <RecommendedBooks userId={user?.userId} maxBooks={20} />
        )}
      </Container>
    </Box>
  );
}

export default SavedBooks;
