import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedText, setSelectedText] = useState('');
const [noteButtonPosition, setNoteButtonPosition] = useState({ top: 0, left: 0 });

  useEffect(() => {
    console.log('bookId:', bookId);
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  const handleTextSelection = () => {
    const selection = window.getSelection();
    const text = selection.toString().trim();
    if (text) {
      const range = selection.getRangeAt(0);
      const rect = range.getBoundingClientRect();
      setSelectedText(text);
      setNoteButtonPosition({ top: rect.top - 30, left: rect.left });
    } else {
      setSelectedText('');
    }
  };

  useEffect(() => {
    const iframe = document.querySelector('iframe');
    if (iframe) {
      const handleLoad = () => {
        iframe.contentDocument?.addEventListener('mouseup', handleTextSelection);
      };
      iframe.addEventListener('load', handleLoad);
      return () => {
        iframe.removeEventListener('load', handleLoad);
        iframe.contentDocument?.removeEventListener('mouseup', handleTextSelection);
      };
    }
  }, []);


  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;

  return (
    <div>
      <div style={{ ...styles.header }}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      {selectedText && (
  <button
    style={{
      position: 'absolute',
      top: `${noteButtonPosition.top}px`,
      left: `${noteButtonPosition.left}px`,
      zIndex: 1000,
    }}
    onClick={() => {/* Handle note creation */}}
  >
    Thêm ghi chú
  </button>
)}
      <iframe
        src={`http://localhost:8080/api/v1/book/book-content?url=${bookData.formats['text/html']}&urlImageCover=${bookData.formats['image/jpeg']}`}
        style={styles.bookContent}
        title="Book Content"
      />
      <div style={{
        position: 'fixed',
        bottom: 0,
        height: '9vh', width: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.0)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        }}>
      <span style={styles.chapterLabel}>Các Chương</span>
      <button
      onClick={()=>{}} style={styles.button}>
        <img src={images.icRightArrow}
          alt="Next" style={styles.icon} />
      </button>
      </div>
    </div>
  );
}

const styles = {
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '19%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '81%',
    marginLeft: '19%',
    height: '100vh',
    border: 'none',
  },
};

export default ReadBookScreen;
