import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import { ReactReader } from 'react-reader';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [readerLocation, setReaderLocation] = useState(null);
  const [notes, setNotes] = useState([]);
  const [epubBlob, setEpubBlob] = useState(null);

  useEffect(() => {
    console.log('bookId:', bookId);
    if (bookId) {
      setLoading(true);
      setError(null);
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => {
          if (!response.ok) throw new Error('Failed to fetch book data');
          return response.json();
        })
        .then((data) => {
          console.log('Book data:', data);
          setBookData(data);
          const epubUrl = data.formats['application/epub+zip'];
          if (!epubUrl) throw new Error('EPUB format not available');
          return fetch(epubUrl);
        })
        .then((response) => {
          if (!response.ok) throw new Error('Failed to fetch EPUB');
          return response.blob();
        })
        .then((blob) => {
          console.log('EPUB blob loaded, size:', blob.size);
          setEpubBlob(blob);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error:', error.message);
          setError(error.message);
          setLoading(false);
        });
    }
  }, [bookId]);

  const locationChanged = (epubcifi) => {
    setReaderLocation(epubcifi);
  };

  const addNote = (content) => {
    const newNote = {
      bookId,
      content,
      location: readerLocation
    };
    setNotes([...notes, newNote]);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!bookData || !epubBlob) {
    return <div>No book data found or EPUB not loaded</div>;
  }

  const { title, authors } = bookData;

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <div style={styles.readerContainer}>
        <ReactReader
          url={URL.createObjectURL(epubBlob)}
          location={readerLocation}
          locationChanged={locationChanged}
          epubOptions={{
            flow: "scrolled",
            manager: "continuous"
          }}
        />
      </div>
      <div style={styles.footer}>
        <span style={styles.chapterLabel}>Các Chương</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
      <div style={styles.noteContainer}>
        <input
          type="text"
          placeholder="Add a note..."
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              addNote(e.target.value);
              e.target.value = '';
            }
          }}
        />
        {notes.map((note, index) => (
          <div key={index}>{note.content}</div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
  },
  header: {
    padding: '10px',
    borderBottom: '1px solid #ddd',
  },
  readerContainer: {
    flex: 1,
    overflow: 'hidden',
  },
  footer: {
    position: 'fixed',
    bottom: 0,
    height: '9vh',
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  noteContainer: {
    position: 'fixed',
    right: 0,
    top: '50%',
    transform: 'translateY(-50%)',
    width: '200px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    padding: '10px',
    borderLeft: '1px solid #ddd',
  },
};

export default ReadBookScreen;
