import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookContent, setBookContent] = useState('');
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    if (bookId) {
      fetch(`/api/proxy?url=https://gutendex.com/books/${bookId}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          const textUrl = data.formats['text/plain; charset=us-ascii'];
          if (textUrl) {
            fetch(textUrl)
              .then((response) => {
                if (!response.ok) {
                  throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.text();
              })
              .then((text) => {
                setBookContent(text);
                // Assume 2000 characters per page for pagination
                const pageSize = 2000;
                setTotalPages(Math.ceil(text.length / pageSize));
                setLoading(false);
              })
              .catch((error) => {
                console.error('Error fetching book content:', error);
                setLoading(false);
              });
          } else {
            setLoading(false);
          }
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  const handlePageChange = (direction) => {
    setCurrentPage((prevPage) => {
      let newPage = prevPage + direction;
      if (newPage < 1) newPage = 1;
      if (newPage > totalPages) newPage = totalPages;
      return newPage;
    });
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  const pageSize = 2000; // Size of each page in characters
  const start = (currentPage - 1) * pageSize;
  const end = start + pageSize;
  const currentContent = bookContent.substring(start, end);

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1>Book Title</h1>
        <p>
          <strong>Author:</strong> Author Name
        </p>
      </div>
      <div style={styles.bookContent}>
        <pre>{currentContent}</pre> {/* Hiển thị nội dung văn bản */}
      </div>
      <div style={styles.footer}>
        <button onClick={() => handlePageChange(-1)} style={styles.button}>
          <img src={images.icLeftArrow} alt="Back" style={styles.icon} />
        </button>
        <span style={styles.chapterLabel}>Page {currentPage} of {totalPages}</span>
        <button onClick={() => handlePageChange(1)} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    overflow: 'auto',
    padding: '0 10px',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '100%',
    maxWidth: '800px',
    margin: '20px auto',
    padding: '20px',
    boxSizing: 'border-box',
    textAlign: 'justify',
  },
  footer: {
    position: 'fixed',
    bottom: 0,
    height: '9vh',
    width: '100%',
    backgroundColor: 'rgba(255, 255, 255, 0.0)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

export default ReadBookScreen;
