import { Button, Checkbox, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, FormControlLabel, InputLabel, MenuItem, Select, TextField } from "@mui/material";

const ReportActionDialog = ({ isOpen, onClose, onSubmit }) => {
  const [action, setAction] = useState('');
  const [showDeleteOption, setShowDeleteOption] = useState(false);
  const [shouldDelete, setShouldDelete] = useState(false);
  const [reason, setReason] = useState('');

  const handleSubmit = () => {
    onSubmit({
      action,
      shouldDelete,
      reason
    });
    onClose();
  };

  const actions = [
    { value: 'DISMISS', label: 'Dismiss Report' },
    { value: 'BAN_1H', label: 'Ban for 1 Hour' },
    { value: 'BAN_3H', label: 'Ban for 3 Hours' },
    { value: 'BAN_24H', label: 'Ban for 24 Hours' }
  ];

  useEffect(() => {
    if (action && action !== 'DISMISS') {
      setShowDeleteOption(true);
    } else {
      setShowDeleteOption(false);
      setShouldDelete(false);
    }
  }, [action]);

  return (
    <Dialog open={isOpen} onClose={onClose}>
      <DialogTitle>Take Action on Report</DialogTitle>
      <DialogContent>
        <FormControl fullWidth margin="normal">
          <InputLabel>Select Action</InputLabel>
          <Select value={action} onChange={(e) => setAction(e.target.value)}>
            {actions.map(opt => (
              <MenuItem key={opt.value} value={opt.value}>
                {opt.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <TextField
          fullWidth
          margin="normal"
          label="Reason"
          multiline
          rows={3}
          value={reason}
          onChange={(e) => setReason(e.target.value)}
        />

        {showDeleteOption && (
          <FormControlLabel
            control={
              <Checkbox
                checked={shouldDelete}
                onChange={(e) => setShouldDelete(e.target.checked)}
              />
            }
            label="Delete this comment"
          />
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSubmit} variant="contained" color="primary">
          Apply Action
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default ReportActionDialog;
