import React, { useState } from 'react';
import { Flag, Check, X, AlertCircle, Clock, Ban, Trash2 } from 'lucide-react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Checkbox, FormControlLabel, FormGroup } from '@mui/material';
import { toast } from 'react-toastify';

const ActionMenu = ({ reportId, forumId }) => {
  const [showBanDialog, setShowBanDialog] = useState(false);
  const [selectedAction, setSelectedAction] = useState(null);
  const [banReason, setBanReason] = useState('');
  const [banTypes, setBanTypes] = useState({
    noInteraction: false,
    noComment: false,
    noJoin: false,
    deleteForum: false
  });

  const actions = [
    { id: 'DISMISS', label: 'Dismiss Report', icon: <X className="w-4 h-4" /> },
    { id: 'WARN', label: 'Warn User', icon: <AlertCircle className="w-4 h-4" /> },
    { id: 'BAN_1H', label: 'Ban User (1h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'BAN_3H', label: 'Ban User (3h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'BAN_24H', label: 'Ban User (24h)', icon: <Clock className="w-4 h-4" /> },
    { id: 'BAN_PERMANENT', label: 'Permanent Ban', icon: <Ban className="w-4 h-4" /> },
  ];

  const handleActionClick = (actionId) => {
    if (actionId === 'DISMISS') {
      handleAction(actionId);
    } else if (actionId === 'WARN') {
      setSelectedAction(actionId);
      setShowBanDialog(true);
    } else if (actionId.startsWith('BAN_')) {
      setSelectedAction(actionId);
      setBanTypes({
        noInteraction: false,
        noComment: false,
        noJoin: false,
        deleteForum: false
      });
      setShowBanDialog(true);
    }
  };

  const handleBanTypeChange = (event) => {
    setBanTypes({
      ...banTypes,
      [event.target.name]: event.target.checked
    });
  };

  const handleReasonSubmit = async () => {
    if (!banReason.trim() && selectedAction !== 'DISMISS') {
      toast.error('Please provide a reason');
      return;
    }

    try {
      // First handle the ban action
      await handleAction(selectedAction, banReason);

      // If delete forum is selected, handle forum deletion
      if (banTypes.deleteForum) {
        await handleDeleteForum();
      }

      setShowBanDialog(false);
      setBanReason('');
      setBanTypes({
        noInteraction: false,
        noComment: false,
        noJoin: false,
        deleteForum: false
      });

      window.location.reload();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.message);
    }
  };

  const handleAction = async (actionId, reason = '') => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/forums/reports/${reportId}/action`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          action: actionId,
          reason: reason || null,
          banTypes: banTypes
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to apply action');
      }

      toast.success('Action applied successfully');
    } catch (error) {
      throw new Error('Failed to apply action: ' + error.message);
    }
  };

  const handleDeleteForum = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/forums/${forumId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete forum');
      }

      toast.success('Forum deleted successfully');
    } catch (error) {
      throw new Error('Failed to delete forum: ' + error.message);
    }
  };

  // Avoid aria-hidden issues with Dialog
  const dialogProps = {
    slotProps: {
      backdrop: {
        'aria-hidden': 'false'
      }
    },
    keepMounted: true,
  };

  return (
    <>
      <div className="absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-[1000]">
        <div className="py-1" role="menu">
          {actions.map((action) => (
            <button
              key={action.id}
              onClick={() => handleActionClick(action.id)}
              className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
              role="menuitem"
            >
              {action.icon}
              <span className="ml-2">{action.label}</span>
            </button>
          ))}
        </div>
      </div>

      <Dialog
        open={showBanDialog}
        onClose={() => setShowBanDialog(false)}
        maxWidth="sm"
        fullWidth
        {...dialogProps}
      >
        <DialogTitle>
          {selectedAction?.includes('BAN') ? 'Ban User' : 'Warning User'}
        </DialogTitle>
        <DialogContent>
          {selectedAction?.includes('BAN') && (
            <FormGroup className="mt-4 mb-4">
              <FormControlLabel
                control={
                  <Checkbox
                    checked={banTypes.noInteraction}
                    onChange={handleBanTypeChange}
                    name="noInteraction"
                  />
                }
                label="Ban user from forum interactions"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={banTypes.noComment}
                    onChange={handleBanTypeChange}
                    name="noComment"
                  />
                }
                label="Ban user from commenting"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={banTypes.noJoin}
                    onChange={handleBanTypeChange}
                    name="noJoin"
                  />
                }
                label="Ban user from joining forums"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={banTypes.deleteForum}
                    onChange={handleBanTypeChange}
                    name="deleteForum"
                  />
                }
                label="Delete this forum"
              />
            </FormGroup>
          )}
          <TextField
            autoFocus
            margin="dense"
            label={selectedAction?.includes('BAN') ? "Reason for ban" : "Warning message"}
            fullWidth
            multiline
            rows={4}
            value={banReason}
            onChange={(e) => setBanReason(e.target.value)}
            variant="outlined"
            required
            sx={{ mt: 2 }}
          />
        </DialogContent>
        <DialogActions sx={{ p: 2 }}>
          <Button
            onClick={() => {
              setShowBanDialog(false);
              setBanReason('');
              setBanTypes({
                noInteraction: false,
                noComment: false,
                noJoin: false,
                deleteForum: false
              });
            }}
            color="inherit"
          >
            Cancel
          </Button>
          <Button
            onClick={handleReasonSubmit}
            disabled={!banReason.trim()}
            variant="contained"
            color="error"
          >
            {selectedAction?.includes('BAN') ? 'Confirm Ban' : 'Send Warning'}
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default ActionMenu;
