import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import { ReactReader } from 'react-reader'

function ReadBookScreen() {
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);

  const [location, setLocation] = useState(null)
const locationChanged = (epubcifi) => {
  setLocation(epubcifi)
}

const [notes, setNotes] = useState([])

const addNote = (content, selectedText, cfiRange) => {
  const newNote = {
    bookId,
    content,
    selectedText,
    location: cfiRange
  }
  // Call your API to save the note
  fetch('http://your-api-url/api/notes', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(newNote),
  })
  .then(response => response.json())
  .then(data => {
    setNotes([...notes, data])
  })
}

  useEffect(() => {
    console.log('bookId:', bookId);
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);


  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors, formats } = bookData;

  return (
    <div>
      <div style={{ ...styles.header }}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <ReactReader
  url={bookData.formats['text/html']}
  location={location}
  locationChanged={locationChanged}
  epubOptions={{
    flow: "scrolled",
    manager: "continuous"
  }}
/>
      <div style={{
        position: 'fixed',
        bottom: 0,
        height: '9vh', width: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.0)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        }}>
      <span style={styles.chapterLabel}>Các Chương</span>
      <button
      onClick={()=>{}} style={styles.button}>
        <img src={images.icRightArrow}
          alt="Next" style={styles.icon} />
      </button>
      </div>
    </div>
  );
}

const styles = {
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '10%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '100%',
    height: '100vh',
    border: 'none',
  },
};

export default ReadBookScreen;
