import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CircularProgress } from '@mui/material';
import { colors } from '../../../constants';

const BookRecommendationsSidebar = () => {
  const [recommendedBooks, setRecommendedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const sidebarWidth = window.innerWidth * 0.2;
  const bookWidth = (sidebarWidth - 40) / 2;

  useEffect(() => {
    const fetchRecommendedBooks = async () => {
      try {
        const response = await fetch('https://gutendex.com/books/?page=1');
        const data = await response.json();
        setRecommendedBooks(data.results.slice(0, 6));
      } catch (error) {
        console.error('Error fetching recommended books:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendedBooks();
  }, []);

  const handleBookClick = (bookId, bookTitle) => {
    navigate('/DescriptionBook', { state: { bookId, bookTitle } });
  };

  return (
    <div
      style={{
        padding: '20px 10px',
      }}
    >
      <h2
        style={{
          color: '#8e8cbb',
          fontSize: '16px',
          fontWeight: 'bold',
          marginBottom: '15px',
          textTransform: 'uppercase',
          paddingLeft: '10px',
        }}
      >
        Sách đề xuất
      </h2>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '20px' }}>
          <CircularProgress size={24} color="primary" />
        </div>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(2, 1fr)',
            gap: '15px',
            justifyContent: 'center',
          }}
        >
          {recommendedBooks.map((book) => (
            <div
              key={book.id}
              onClick={() => handleBookClick(book.id, book.title)}
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                cursor: 'pointer',
              }}
            >
              <div
                style={{
                  height: bookWidth * 0.98,
                  width: bookWidth,
                  backgroundImage: `url('${book.formats['image/jpeg']}')`,
                  borderRadius: 4,
                  backgroundSize: 'cover',
                  borderWidth: 2,
                  borderColor: '#474a51',
                  borderStyle: 'solid',
                  boxShadow: '3px 3px 6px rgba(255, 255, 255, 0.2)',
                }}
              />
              <span
                style={{
                  marginTop: 8,
                  width: bookWidth,
                  fontWeight: 'bold',
                  fontSize: '12px',
                  color: colors.themeLight.textColor,
                  display: '-webkit-box',
                  WebkitBoxOrient: 'vertical',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  WebkitLineClamp: 2,
                  textAlign: 'center',
                }}
              >
                {book.title}
              </span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BookRecommendationsSidebar;
