import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { images } from '../../../constants';
import ReactHtmlParser from 'react-html-parser';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};

  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [bookContent, setBookContent] = useState('');

  useEffect(() => {
    if (bookId) {
      // Fetch book data
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          // Fetch book content in HTML format
          const htmlUrl = data.formats['text/html'];
          if (htmlUrl) {
            fetch(`/api/proxy${htmlUrl}`)
              .then((response) => response.text())
              .then((htmlData) => {
                // Extract only the first 50 lines of the HTML content
                const lines = htmlData.split('\n');
                const first50Lines = lines.slice(0, 50).join('\n');
                setBookContent(first50Lines);
              })
              .catch((error) => console.error('Error fetching book content:', error));
          }
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoading(false);
        });
    }
  }, [bookId]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  const { title, authors } = bookData;

  return (
    <div>
      <div style={styles.header}>
        <h1>{title}</h1>
        <p>
          <strong>Author:</strong> {authors.map((author) => author.name).join(', ')}
        </p>
      </div>
      <div style={styles.bookContent}>
        {/* Display first 50 lines of book content */}
        {ReactHtmlParser(bookContent)}
      </div>
      <div style={{
        position: 'fixed',
        bottom: 0,
        height: '9vh', width: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.0)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icLeftArrow} alt="Back" style={styles.icon} />
        </button>
        <span style={styles.chapterLabel}>Chương 1</span>
        <button onClick={() => {}} style={styles.button}>
          <img src={images.icRightArrow} alt="Next" style={styles.icon} />
        </button>
      </div>
    </div>
  );
}

const styles = {
  icon: {
    width: '30px',
    height: '30px',
  },
  button: {
    padding: '10px 20px',
    cursor: 'pointer',
  },
  chapterLabel: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    width: '19%',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    zIndex: 1000,
    transition: 'opacity 0.3s ease',
  },
  bookContent: {
    width: '100%',
    height: '100vh',
    overflow: 'auto', // Ensure that long content can be scrolled
    padding: '20px',
    boxSizing: 'border-box',
  },
};

export default ReadBookScreen;
