import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';

function ReadBookScreen() {
  const location = useLocation();
  const { bookId } = location.state || {};
  const [bookData, setBookData] = useState(null);
  const [chapters, setChapters] = useState([]);
  const [loadingBook, setLoadingBook] = useState(true);
  const [loadingChapters, setLoadingChapters] = useState(true);

  // Fetch the book data
  useEffect(() => {
    if (bookId) {
      fetch(`https://gutendex.com/books/${bookId}`)
        .then((response) => response.json())
        .then((data) => {
          setBookData(data);
          setLoadingBook(false);
        })
        .catch((error) => {
          console.error('Error fetching book data:', error);
          setLoadingBook(false);
        });
    }
  }, [bookId]);

  // Fetch chapters separately
  useEffect(() => {
    if (bookData && bookData.formats) {
      const bookUrl = bookData.formats['text/html'];
      fetch(`http://localhost:8080/api/v1/book/chapters?url=${bookUrl}`)
        .then((response) => response.json())
        .then((chapterData) => {
          setChapters(chapterData);
          setLoadingChapters(false);
        })
        .catch((error) => {
          console.error('Error fetching chapters:', error);
          setLoadingChapters(false);
        });
    }
  }, [bookData]);

  if (loadingBook) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!bookData) {
    return <div>No book data found</div>;
  }

  return (
    <div>
      <h1>{bookData.title}</h1>
      <p>Author: {bookData.authors.map((author) => author.name).join(', ')}</p>
      <div>
        {loadingChapters ? (
          <CircularProgress />
        ) : (
          <div>
            <h2>Chapters:</h2>
            <ul>
              {chapters.map((chapter) => (
                <li key={chapter.id}>{chapter.name}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default ReadBookScreen;
