import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { format } from 'date-fns';
import { Avatar, Menu, MenuItem, IconButton } from '@mui/material';
import { MoreVertical, Flag } from 'lucide-react';

const CommentReports = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedReport, setSelectedReport] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);

  function stringToColor(string) {
    let hash = 0;
    for (let i = 0; i < string.length; i++) {
      hash = string.charCodeAt(i) + ((hash << 5) - hash);
    }
    let color = '#';
    for (let i = 0; i < 3; i++) {
      const value = (hash >> (i * 8)) & 0xff;
      color += `00${value.toString(16)}`.slice(-2);
    }
    return color;
  }

  function stringAvatar(name) {
    return {
      sx: {
        bgcolor: stringToColor(name),
        width: 33,
        height: 33,
      },
      children: `${name.split(' ')[0][0]}${name.split(' ')[1][0]}`,
    };
  }

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_BASE_URL}/comment-reports/pending`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setReports(data.data);
      }
    } catch (error) {
      toast.error('Failed to fetch comment reports');
    } finally {
      setLoading(false);
    }
  };

  const handleAction = async (reportId, action) => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_BASE_URL}/api/v1/comment-reports/reports/${reportId}/action`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
          body: JSON.stringify({
            action,
            banTypes: {
              noComment: true,
              deleteComment: action !== 'DISMISS',
            },
          }),
        }
      );

      if (response.ok) {
        toast.success('Action applied successfully');
        fetchReports();
      } else {
        throw new Error('Failed to apply action');
      }
    } catch (error) {
      toast.error(error.message);
    }
    setAnchorEl(null);
  };

  const StatusBadge = ({ status }) => {
    const colors = {
      PENDING: 'bg-yellow-100 text-yellow-800',
      DISMISSED: 'bg-gray-100 text-gray-800',
      BANNED: 'bg-red-100 text-red-800',
    };

    return (
      <span className={`px-3 py-1 rounded-full text-sm font-medium ${colors[status]}`}>
        {status}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500" />
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6 flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold">Comment Reports</h1>
          <p className="text-gray-600">Review and manage reported comments</p>
        </div>
        <div className="flex items-center gap-2">
          <div className="bg-white p-3 rounded-lg shadow-sm flex items-center gap-2">
            <Flag className="w-5 h-5 text-red-500" />
            <span className="font-medium">{reports.length} Pending Reports</span>
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Comment Author
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Reporter
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Content & Reason
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {reports.map((report) => (
                <tr key={report.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4">
                    <div className="flex items-center">
                      {report.commentAuthor.urlAvatar ? (
                        <Avatar
                          sx={{ width: 33, height: 33 }}
                          src={report.commentAuthor.urlAvatar}
                          alt={report.commentAuthor.fullName}
                        />
                      ) : (
                        <Avatar {...stringAvatar(report.commentAuthor.fullName)} />
                      )}
                      <div className="ml-4">
                        <div className="text-sm font-medium text-gray-900">
                          {report.commentAuthor.fullName}
                        </div>
                        <div className="text-sm text-gray-500">
                          {report.commentAuthor.email}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center">
                      {report.reporter.urlAvatar ? (
                        <Avatar
                          sx={{ width: 33, height: 33 }}
                          src={report.reporter.urlAvatar}
                          alt={report.reporter.fullName}
                        />
                      ) : (
                        <Avatar {...stringAvatar(report.reporter.fullName)} />
                      )}
                      <div className="ml-4">
                        <div className="text-sm font-medium text-gray-900">
                          {report.reporter.fullName}
                        </div>
                        <div className="text-sm text-gray-500">{report.reporter.email}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="max-w-xl">
                      <div className="text-sm text-gray-900 mb-2 line-clamp-2">
                        {report.commentContent}
                      </div>
                      <div className="text-xs font-medium text-red-600 bg-red-50 rounded-full px-2 py-1 inline-block">
                        {report.reason.replace(/_/g, ' ')}
                      </div>
                      {report.additionalInfo && (
                        <div className="text-sm text-gray-500 mt-1 italic">
                          "{report.additionalInfo}"
                        </div>
                      )}
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <StatusBadge status={report.status} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {format(new Date(report.reportedAt), 'PPp')}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    {report.status === 'PENDING' && (
                      <>
                        <IconButton
                          onClick={(event) => {
                            setSelectedReport(report);
                            setAnchorEl(event.currentTarget);
                          }}
                        >
                          <MoreVertical className="w-5 h-5" />
                        </IconButton>
                        <Menu
                          anchorEl={anchorEl}
                          open={Boolean(anchorEl) && selectedReport?.id === report.id}
                          onClose={() => setAnchorEl(null)}
                        >
                          <MenuItem onClick={() => handleAction(report.id, 'DISMISS')}>
                            Dismiss Report
                          </MenuItem>
                          <MenuItem onClick={() => handleAction(report.id, 'BAN_24H')}>
                            Ban 24 Hours
                          </MenuItem>
                          <MenuItem onClick={() => handleAction(report.id, 'BAN_PERMANENT')}>
                            Ban Permanently
                          </MenuItem>
                        </Menu>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CommentReports;
