import React, { useState, useEffect, useRef } from 'react';
import { getToken, onMessage } from 'firebase/messaging';
import { messaging } from '../../../config/firebase'; // Import tá»« file config
import {
  Bell,
  Flag,
  AlertTriangle,
  Ban,
  MessageCircle,
  UserPlus,
  X,
  Check,
  ExternalLink
} from 'lucide-react';
import { format } from 'date-fns';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

const NotificationDropdown = () => {
  const [notifications, setNotifications] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const dropdownRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const initializeNotifications = async () => {
      try {
        setLoading(true);

        const permission = await Notification.requestPermission();
        if (permission === 'granted') {
          // Get FCM token
          const token = await getToken(messaging, {
            vapidKey: process.env.REACT_APP_FIREBASE_VAPID_KEY
          });

          if (token) {
            try {
              const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/notifications/register-device`, {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ fcmToken: token })
              });

              if (!response.ok) {
                const errorData = await response.json();
                console.error('Error response:', errorData);
                throw new Error(errorData.message || 'Failed to register device');
              }

              const data = await response.json();
              console.log('Registration successful:', data);
            } catch (error) {
              console.error('Registration error:', error);
              throw error;
            }
          }

          // Listen for foreground messages
          const unsubscribe = onMessage(messaging, (payload) => {
            console.log('Received foreground message:', payload);
            const newNotification = {
              id: Date.now().toString(),
              title: payload.notification.title,
              message: payload.notification.body,
              type: payload.data?.type || 'DEFAULT',
              data: JSON.parse(payload.data?.extraData || '{}'),
              createdAt: new Date().toISOString(),
              read: false
            };

            setNotifications(prev => [newNotification, ...prev]);
            setUnreadCount(prev => prev + 1);
          });

          return () => {
            unsubscribe();
          };
        }
      } catch (error) {
        console.error('Error initializing notifications:', error);
      } finally {
        setLoading(false);
      }
    };

    initializeNotifications();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const markAsRead = async (notificationId) => {
    setNotifications(prev =>
      prev.map(notification =>
        notification.id === notificationId
          ? { ...notification, read: true }
          : notification
      )
    );
    setUnreadCount(prev => Math.max(0, prev - 1));
  };

  const markAllAsRead = () => {
    setNotifications(prev =>
      prev.map(notification => ({ ...notification, read: true }))
    );
    setUnreadCount(0);
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'FORUM_REPORT':
        return <Flag className="w-5 h-5 text-red-500" />;
      case 'WARNING':
        return <AlertTriangle className="w-5 h-5 text-yellow-500" />;
      case 'USER_BANNED':
        return <Ban className="w-5 h-5 text-purple-500" />;
      case 'NEW_COMMENT':
        return <MessageCircle className="w-5 h-5 text-blue-500" />;
      case 'NEW_MEMBER':
        return <UserPlus className="w-5 h-5 text-green-500" />;
      default:
        return <Bell className="w-5 h-5 text-gray-500" />;
    }
  };

  const getNotificationLink = (notification) => {
    switch (notification.type) {
      case 'FORUM_REPORT':
        return `/admin/forum-reports/${notification.data.reportId}`;
      case 'USER_BANNED':
        return `/admin/users/${notification.data.userId}`;
      case 'NEW_COMMENT':
        return `/forums/${notification.data.forumId}`;
      case 'NEW_MEMBER':
        return `/forums/${notification.data.forumId}/members`;
      default:
        return null;
    }
  };

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 text-gray-600 hover:text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 rounded-full"
      >
        <Bell className="w-6 h-6" />
        {unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 text-white text-xs flex items-center justify-center rounded-full">
            {unreadCount > 99 ? '99+' : unreadCount}
          </span>
        )}
      </button>

      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: -10, scale: 0.95 }}
            transition={{ duration: 0.2 }}
            className="absolute right-0 mt-2 w-96 bg-white rounded-lg shadow-xl z-50 border border-gray-200"
          >
            <div className="flex items-center justify-between px-4 py-3 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">ThÃ´ng bÃ¡o</h3>
              <div className="flex items-center gap-2">
                {unreadCount > 0 && (
                  <button
                    onClick={markAllAsRead}
                    className="text-sm text-blue-600 hover:text-blue-800"
                    title="ÄÃ¡nh dáº¥u táº¥t cáº£ lÃ  ÄÃ£ Äá»c"
                  >
                    <Check className="w-5 h-5" />
                  </button>
                )}
                <button
                  onClick={() => setIsOpen(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>
            </div>

            <div className="overflow-y-auto max-h-96">
              {loading ? (
                <div className="flex justify-center items-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500" />
                </div>
              ) : notifications.length === 0 ? (
                <div className="flex flex-col items-center justify-center py-8 text-gray-500">
                  <Bell className="w-12 h-12 mb-2" />
                  <p>ChÆ°a cÃ³ thÃ´ng bÃ¡o nÃ o</p>
                </div>
              ) : (
                notifications.map((notification) => {
                  const notificationLink = getNotificationLink(notification);
                  const NotificationWrapper = notificationLink ? Link : 'div';
                  const wrapperProps = notificationLink ? { to: notificationLink } : {};

                  return (
                    <NotificationWrapper
                      key={notification.id}
                      {...wrapperProps}
                      className={`p-4 border-b border-gray-100 hover:bg-gray-50 transition-colors block
                        ${!notification.read ? 'bg-blue-50' : ''}`}
                      onClick={() => !notification.read && markAsRead(notification.id)}
                    >
                      <div className="flex gap-3">
                        <div className="flex-shrink-0 mt-1">
                          {getNotificationIcon(notification.type)}
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-start justify-between">
                            <p className="text-sm font-semibold text-gray-900">
                              {notification.title}
                            </p>
                            <span className="text-xs text-gray-500">
                              {format(new Date(notification.createdAt), 'dd/MM/yyyy HH:mm')}
                            </span>
                          </div>
                          <p className="mt-1 text-sm text-gray-600">
                            {notification.message}
                          </p>
                        </div>
                      </div>
                    </NotificationWrapper>
                  );
                })
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default NotificationDropdown;
