import React, {useState, useEffect} from 'react'
import {Link, useNavigate, useLocation} from 'react-router-dom'
import {images, colors} from '../../../constants'
import axios from 'axios'
import {Avatar,  Button as MuiButton, Menu, MenuItem, ListItemIcon} from '@mui/material'
import {AccountCircle, ExitToApp, Settings} from '@mui/icons-material'
import {toast} from 'react-toastify'
// import { CardContent } from '@/components/ui/card';
import { Star, BookOpen } from 'lucide-react';
import { Card, CardContent } from "../../../components/ui/card"
import { Button } from "../../../components/ui/button"

export default function DescriptionBook( ) {
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  })

  const [anchorEl, setAnchorEl] = useState(null)
  const [user, setUser] = useState(null)
  const [bookDetails, setBookDetails] = useState(null);
  const [rating, setRating] = useState(0);
  const [hover, setHover] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const open = Boolean(anchorEl)
  const navigate = useNavigate()

  const location = useLocation()
  const { bookId, bookTitle } = location.state || {}

  const handleSearch = event => {
    const value = event.target.value
  }

  const handleClick = event => {
    setAnchorEl(event.currentTarget)
  }

  const handleClose = () => {
    setAnchorEl(null)
  }

  const handleLogout = () => {
    handleClose()
    localStorage.removeItem('token')
    setUser(null)
    navigate('/')
    toast.success('Logout successfully')
  }

  const getUser = async () => {
    try {
      const response = await axios.get(
        'http://localhost:8080/api/v1/user/profile',
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        },
      )
      setUser(response.data)
    } catch (error) {
      console.error(error)
    }
  }

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await fetch(`https://gutendex.com/books/${bookId}/`);
        if (!response.ok) {
          throw new Error('Failed to fetch book details');
        }
        const data = await response.json();
        setBookDetails(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch book details');
        setLoading(false);
      }
    };

    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    fetchBookDetails();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [bookId]);

  const handleRatingClick = (value) => {
    setRating(value);
  };

  const renderStarRating = () => (
    <div className="flex items-center gap-1">
      {[1, 2, 3, 4, 5].map((star) => (
        <Star
          key={star}
          className={`w-6 h-6 cursor-pointer transition-colors ${
            (hover || rating) >= star
              ? 'fill-yellow-400 text-yellow-400'
              : 'text-gray-300'
          }`}
          onMouseEnter={() => setHover(star)}
          onMouseLeave={() => setHover(0)}
          onClick={() => handleRatingClick(star)}
        />
      ))}
    </div>
  );

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      })
    }

    getUser()

    window.addEventListener('resize', handleResize)
    return () => {
      window.removeEventListener('resize', handleResize)
    }
  }, [])


  return (
    <div
      className='App'
      style={{
        backgroundColor: colors.themeLight.color060d13,
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
      }}>
      <div
        style={{
          display: 'flex',
          height: windowSize.height * 0.09,
          width: windowSize.width,
          flexDirection: 'row',
          paddingTop: 5,
          position: 'fixed',
          top: 0,
          zIndex: 1,
          boxShadow: '0px 5px 5px rgba(0, 0, 0, 0.2)',
          alignItems: 'center',
        }}>
        <div className='flex gap-2 items-center p-5'>
          <img
            src={images.imgOpenBook}
            alt='Logo Open Book'
            style={{
              height: windowSize.height * 0.09 - 32,
              marginLeft: 15,
              marginRight: 15,
            }}
          />
          <Link to='/'>
            <span className='text-sm font-medium '>READHUB</span>
          </Link>
        </div>
       <span style={{
        display: 'flex',
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
       }}>
          {bookTitle}
       </span>

        {user ? (
          <Button
            style={{
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'center',
              alignItems: 'center',
              gap: 10,
              marginRight: 20,
            }}
            onClick={handleClick}>
            {user.urlAvatar ? (
              <Avatar src={user.urlAvatar} />
            ) : (
              <Avatar>{user.username ? user.username.charAt(0) : 'U'}</Avatar>
            )}
            <span>{user.username}</span>
          </Button>
        ) : (
          <div style={{display: 'flex', flexDirection: 'row'}}>
            <Button
              style={{
                color: '#fff',
                padding: '5px 13px',
                borderRadius: 19,
                height: 40,
                display: 'flex',
                flexDirection: 'row',
                backgroundColor: 'lightgray',
                justifyContent: 'center',
                alignItems: 'center',
                marginRight: 20,
              }}>
              <Link to='/Register'>
                <div
                  style={{
                    fontSize: 15,
                    fontWeight: 'bold',
                    textTransform: 'none',
                  }}>
                  Register
                </div>
              </Link>
            </Button>
            <Button
              style={{
                color: '#fff',
                backgroundColor: '#51bd8e',
                borderRadius: 19,
                height: 40,
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'center',
                alignItems: 'center',
                marginRight: 20,
              }}>
              <Link to='/LoginAccount'>
                <div
                  style={{
                    fontSize: 15,
                    fontWeight: 'bold',
                    textTransform: 'none',
                  }}>
                  Login to ReadHub
                </div>
              </Link>
            </Button>
          </div>
        )}
      </div>
      <div style={{
        display: 'flex',
        flex: 1,
        marginTop: windowSize.height * 0.09,
        justifyContent: 'center',
        alignItems: 'center',
      }}>
       <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto pt-24 px-4">
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-gray-900"></div>
          </div>
        ) : error ? (
          <div className="text-red-500 text-center">{error}</div>
        ) : bookDetails && (
          <div className="flex flex-col md:flex-row gap-8">
            <div className="md:w-1/3">
              <Card>
                <CardContent className="p-4">
                  <img
                    src={bookDetails.formats['image/jpeg']}
                    alt={bookDetails.title}
                    className="w-full rounded-lg shadow-lg"
                  />
                </CardContent>
              </Card>
            </div>

            <div className="md:w-2/3">
              <Card>
                <CardContent className="p-6 space-y-4">
                  <h1 className="text-3xl font-bold text-gray-900">{bookDetails.title}</h1>

                  <div className="space-y-2">
                    <p className="text-lg text-gray-600">
                      <span className="font-semibold">Author: </span>
                      {bookDetails.authors.map(author => author.name).join(', ')}
                    </p>

                    <p className="text-lg text-gray-600">
                      <span className="font-semibold">Genres: </span>
                      {bookDetails.subjects.slice(0, 3).join(', ')}
                    </p>

                    <p className="text-lg text-gray-600">
                      <span className="font-semibold">Publisher: </span>
                      Project Gutenberg
                    </p>

                    <p className="text-lg text-gray-600">
                      <span className="font-semibold">Language: </span>
                      {bookDetails.languages.map(lang => lang.toUpperCase()).join(', ')}
                    </p>

                    <p className="text-lg text-gray-600">
                      <span className="font-semibold">Downloads: </span>
                      {bookDetails.download_count.toLocaleString()}
                    </p>
                  </div>

                  <div className="space-y-4 pt-4">
                    <div className="flex items-center gap-2">
                      <span className="font-semibold text-lg">Rate this book:</span>
                      {renderStarRating()}
                    </div>

                    <Button
                      className="w-full md:w-auto bg-blue-600 hover:bg-blue-700 text-white px-8 py-2 rounded-lg flex items-center justify-center gap-2"
                      onClick={() => window.open(bookDetails.formats['text/html'], '_blank')}
                    >
                      <BookOpen className="w-5 h-5" />
                      Read Book
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        )}
      </div>
    </div>
        </div>
      </div>
      );

}
