import React, { useEffect, useState, useCallback, useRef } from 'react';

const ListBook = ({ searchTerm, windowSize }) => {
  const [books, setBooks] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [shouldFetch, setShouldFetch] = useState(true);
  const [error, setError] = useState(null);
  const prevSearchTerm = useRef('');

  const fetchBooks = useCallback(
    async (search = '', pageNum = 1) => {
      setHasMore(true);
      if (loading || !shouldFetch) return;
      setLoading(true);
      setError(null);
      try {
        const formattedSearchTerm = search.replace(/ /g, '&');
        const apiUrl = `https://gutendex.com/books/?search=${formattedSearchTerm}&page=${pageNum}`;
        const response = await fetch(apiUrl);
        const data = await response.json();

        if (Array.isArray(data.results)) {
          if (pageNum === 1) {
            setBooks(data.results);
          } else {
            setBooks(prevBooks => [...prevBooks, ...data.results]);
          }
          setPage(pageNum + 1);
          setHasMore(data.next !== null);
        } else {
          setHasMore(false);
        }
      } catch (err) {
        setError('Failed to load books. Please try again later.');
        setHasMore(false);
      } finally {
        setLoading(false);
        setShouldFetch(false);
      }
    },
    [loading, shouldFetch]
  );

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop >=
        document.documentElement.offsetHeight - 100 &&
        hasMore
      ) {
        setShouldFetch(true);
      }
    };

    window.addEventListener('scroll', handleScroll);

    let searchTimeoutId;

    if (searchTerm !== prevSearchTerm.current) {
      prevSearchTerm.current = searchTerm;
      if (searchTimeoutId) {
        clearTimeout(searchTimeoutId);
      }
      searchTimeoutId = setTimeout(() => {
        setPage(1);
        setBooks([]);
        setShouldFetch(true);
      }, 200);
    }

    if (shouldFetch) {
      fetchBooks(searchTerm, page);
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
      if (searchTimeoutId) {
        clearTimeout(searchTimeoutId);
      }
    };
  }, [searchTerm, fetchBooks, page, shouldFetch, hasMore]);

  const handleBookClick = (bookId, bookTitle) => {
    // Handle navigation through props or context instead of router
    console.log('Book clicked:', bookId, bookTitle);
  };

  if (error) {
    return (
      <div className="m-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
        {error}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-white-900 to-slate-900 p-4 sm:p-6">
      <div className="max-w-7xl mx-auto">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
          {books.map((book, index) => (
            <div
              key={`${book.id}-${index}`}
              onClick={() => handleBookClick(book.id, book.title)}
              className="group relative flex flex-col transition-transform duration-300 hover:scale-105"
            >
              <div className="relative aspect-[2/3] rounded-lg overflow-hidden shadow-lg">
                <div
                  className="absolute inset-0 bg-cover bg-center transition-transform duration-300 group-hover:scale-110"
                  style={{
                    backgroundImage: `url('${book.formats['image/jpeg']}')`
                  }}
                />
                <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 transition-opacity duration-300" />
                <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <button className="bg-white bg-opacity-90 px-4 py-2 rounded-full text-sm font-semibold text-gray-900 hover:bg-opacity-100 transform -translate-y-2 group-hover:translate-y-0 transition-transform duration-300">
                    View Details
                  </button>
                </div>
              </div>
              <h3 className="mt-4 text-center text-sm font-medium text-white line-clamp-2">
                {book.title}
              </h3>
              <p className="mt-1 text-center text-sm text-gray-400">
                {book.authors?.[0]?.name || 'Unknown Author'}
              </p>
            </div>
          ))}

          {loading &&
            [...Array(6)].map((_, index) => (
              <div key={`skeleton-${index}`} className="flex flex-col">
                <div className="aspect-[2/3] rounded-lg bg-gray-700 animate-pulse" />
                <div className="mt-4 h-4 bg-gray-700 rounded animate-pulse" />
                <div className="mt-2 h-4 w-2/3 mx-auto bg-gray-700 rounded animate-pulse" />
              </div>
            ))}
        </div>

        {loading && (
          <div className="flex items-center justify-center mt-8">
            <div className="animate-spin rounded-full h-8 w-8 border-4 border-white border-t-transparent" />
            <span className="ml-3 text-white">Loading more books...</span>
          </div>
        )}

        {!hasMore && books.length > 0 && (
          <p className="text-center text-gray-400 mt-8">
            No more books to load
          </p>
        )}

        {!hasMore && books.length === 0 && !loading && (
          <div className="text-center mt-16">
            <p className="text-lg text-gray-400">
              No books found. Try a different search term.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ListBook;
