import React, {useState, useEffect} from 'react'
import Header from '../component/header/HeaderComponent'
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  IconButton,
  Box,
  CircularProgress,
  Alert,
  Button,
  Tabs,
  Tab,
} from '@mui/material'
import {
  BookOpen,
  BookmarkMinus,
  History,
  BookmarkPlus,
  ThumbsUp,
} from 'lucide-react'
import {useNavigate} from 'react-router-dom'
import axios from 'axios'
import {colors} from '../constants'

// Book Card Component
const BookCard = ({book, onUnsave, showUnsave = true, onClick}) => (
  <Card
    sx={{
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
      transition: 'transform 0.2s',
      bgcolor: 'rgba(255, 255, 255, 0.05)',
      backdropFilter: 'blur(10px)',
      '&:hover': {
        transform: 'scale(1.05)',
        bgcolor: 'rgba(255, 255, 255, 0.1)',
      },
    }}>
    <Box sx={{position: 'relative', pt: '150%'}}>
      <CardMedia
        component='img'
        image={book.formats['image/jpeg']}
        alt={book.title}
        sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          objectFit: 'cover',
        }}
      />
      {showUnsave && (
        <Box sx={{position: 'absolute', top: 0, right: 0, p: 1}}>
          <IconButton
            onClick={e => {
              e.stopPropagation()
              onUnsave(book.id)
            }}
            sx={{
              bgcolor: 'rgba(0, 0, 0, 0.5)',
              '&:hover': {bgcolor: 'rgba(0, 0, 0, 0.7)'},
            }}>
            <BookmarkMinus size={20} color='white' />
          </IconButton>
        </Box>
      )}
      <Box
        sx={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          bgcolor: 'rgba(0, 0, 0, 0.7)',
          p: 1,
          display: 'flex',
          justifyContent: 'center',
        }}>
        <IconButton onClick={onClick} sx={{color: 'white'}}>
          <BookOpen size={20} />
        </IconButton>
      </Box>
    </Box>
    <CardContent sx={{flexGrow: 1, p: 1.5}}>
      <Typography
        variant='subtitle2'
        component='h2'
        color='black'
        sx={{
          fontSize: '0.875rem',
          fontWeight: 'bold',
          mb: 0.5,
          display: '-webkit-box',
          WebkitLineClamp: 2,
          WebkitBoxOrient: 'vertical',
          overflow: 'hidden',
          lineHeight: 1.2,
        }}>
        {book.title}
      </Typography>
      <Typography
        variant='caption'
        color='grey.400'
        sx={{
          display: '-webkit-box',
          WebkitLineClamp: 1,
          WebkitBoxOrient: 'vertical',
          overflow: 'hidden',
        }}>
        {book.authors.map(author => author.name).join(', ')}
      </Typography>
    </CardContent>
  </Card>
)

// Recently Read Books Component
const RecentlyReadBooks = ({userId, maxBooks = 20}) => {
  const [recentBooks, setRecentBooks] = useState([])
  const [loading, setLoading] = useState(true)
  const navigate = useNavigate()

  useEffect(() => {
    const fetchRecentBooks = async () => {
      try {
        const historyResponse = await axios.get(
          `http://localhost:8080/api/v1/reading-history/user/${userId}`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          },
        )

        if (historyResponse.data.success) {
          const bookIds = historyResponse.data.data.slice(0, maxBooks)
          const bookPromises = bookIds.map(bookId =>
            fetch(`https://gutendex.com/books/${bookId}/`).then(res =>
              res.json(),
            ),
          )
          const books = await Promise.all(bookPromises)
          setRecentBooks(books)
        }
        setLoading(false)
      } catch (error) {
        console.error('Error fetching recent books:', error)
        setLoading(false)
      }
    }

    if (userId) {
      fetchRecentBooks()
    }
  }, [userId, maxBooks])

  if (loading) {
    return <CircularProgress />
  }

  if (recentBooks.length === 0) {
    return (
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          height: '50vh',
          gap: 2,
        }}>
        <Typography variant='h6' color='white'>
          No reading history yet
        </Typography>
        <Button variant='contained' onClick={() => navigate('/')}>
          Start Reading
        </Button>
      </Box>
    )
  }

  return (
    <Grid container spacing={3}>
      {recentBooks
        .filter(book => book !== null) // Filter out null values
        .map(book => (
          <Grid item xs={12} sm={6} md={3} key={book.id}>
            <BookCard
              book={book}
              showUnsave={false}
              onClick={() =>
                navigate('/description-book', {
                  state: {bookId: book.id, bookTitle: book.title},
                })
              }
            />
          </Grid>
        ))}
    </Grid>
  )
}

