import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { Plus, Search, MessageSquare } from 'lucide-react';
import { images } from '../../constants';

const Header = ({ windowSize, centerContent , showCreateForums = true, showSearch = true, onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [user, setUser] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleSearch = (event) => {
    const value = event.target.value;
    setSearchTerm(value);
    if (onSearch) {
      onSearch(value);
    }
  };

  const handleLogout = () => {
    setIsMenuOpen(false);
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
    toast.success('Logout successfully');
  };

  const getUser = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/v1/user/profile', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setUser(data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getUser();
  }, []);

  return (
    <div className="fixed top-0 z-50 w-full bg-white border-b border-gray-200 shadow-sm">
      <div className="container mx-auto px-4">
        <div className="flex h-16 items-center justify-between">
          <div className="flex items-center space-x-4">
            <Link to="/" className="flex items-center space-x-2">
              <img
                src={images.imgOpenBook}
                alt="ReadHub Logo"
                className="h-8 w-8"
              />
              <span className="font-bold text-xl text-gray-900">READHUB</span>
            </Link>

            <div className="flex items-center space-x-2">
              {(user != null & showCreateForums== true)&& (
                <Link
                  to="/create-forum"
                  className="inline-flex items-center px-4 py-2 bg-green-600 text-white rounded-full hover:bg-green-700 transition-colors"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  <span>Create Forum</span>
                </Link>
              )}

              <Link
                to="/book-forum"
                className="inline-flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-full transition-colors"
              >
                <MessageSquare className="w-4 h-4 mr-2" />
                <span>Forums</span>
              </Link>
            </div>
          </div>

          {showSearch && (
            <div className="flex-1 max-w-xl mx-8">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <input
                  type="text"
                  placeholder="Search books, authors, or keywords..."
                  value={searchTerm}
                  onChange={handleSearch}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-full bg-gray-50 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                />
              </div>
            </div>
          )}

          {centerContent && (
            <div className="flex-1 flex justify-center">
              {centerContent}
            </div>
          )}

          {/* Right section - Auth buttons or User menu */}
          <div className="flex items-center space-x-4">
            {user ? (
              <div className="relative">
                <button
                  onClick={() => setIsMenuOpen(!isMenuOpen)}
                  className="flex items-center space-x-3 p-2 rounded-full hover:bg-gray-100 transition-colors"
                >
                  <div className="w-8 h-8 rounded-full bg-green-600 flex items-center justify-center text-white">
                    {user.urlAvatar ? (
                      <img
                        src={user.urlAvatar}
                        alt={user.username}
                        className="w-8 h-8 rounded-full"
                      />
                    ) : (
                      <span className="font-medium">
                        {user.username ? user.username.charAt(0).toUpperCase() : 'U'}
                      </span>
                    )}
                  </div>
                  <span className="font-medium text-gray-700">{user.username}</span>
                </button>

                {isMenuOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-1 border border-gray-200">
                    <Link
                      to="/profile"
                      className="block px-4 py-2 text-gray-700 hover:bg-gray-100"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      Profile
                    </Link>
                    <Link
                      to="/settings"
                      className="block px-4 py-2 text-gray-700 hover:bg-gray-100"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      Settings
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link
                  to="/register"
                  className="px-6 py-2 text-gray-700 hover:bg-gray-100 rounded-full transition-colors"
                >
                  Register
                </Link>
                <Link
                  to="/login-account"
                  className="px-6 py-2 bg-green-600 text-white rounded-full hover:bg-green-700 transition-colors"
                >
                  Login to ReadHub
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
