import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './App.css';
import { images, colors } from './constants';
import ListBook from './ListBook';

function App() {
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = event => {
    const value = event.target.value;
    setSearchTerm(value);
  };

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div
      className='App'
      style={{
        backgroundColor: colors.themeDark.color060d13,
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
      }}
    >
      <div
        style={{
          display: 'flex',
          height: windowSize.height * 0.09,
          width: windowSize.width - 15,
          flexDirection: 'row',
          paddingTop: 5,
          position: 'fixed',
          top: 0,
          zIndex: 1,
          boxShadow: '0px 5px 5px rgba(0, 0, 0, 0.2)',
        }}
      >
        <img
          src={images.imgOpenBook}
          alt='Logo Open Book'
          style={{
            height: windowSize.height * 0.09 - 32,
            marginTop: 13,
            marginBottom: 13,
            marginLeft: 15,
            marginRight: 15,
          }}
        />
        <form
          style={{
            flex: 1,
            height: windowSize.height * 0.09,
          }}
          action='/search'
        >
          <div
            style={{
              height: windowSize.height * 0.09 - 6,
              alignItems: 'center',
              justifyContent: 'center',
              display: 'flex',
              flexDirection: 'row',
            }}
          >
            <input
              type='text'
              placeholder='Tìm kiếm theo tên sách'
              value={searchTerm}
              onChange={handleSearch}
              style={{
                height: windowSize.height * 0.09 - 25,
                width: windowSize.width * 0.32,
                padding: '10px',
                border: '1px solid #ccc',
                borderRadius: '4px',
                fontSize: '16px',
              }}
            />
          </div>
        </form>
      </div>

      <div
        style={{
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          marginTop: windowSize.height * 0.09,
        }}
      >
        <ListBook searchTerm={searchTerm} windowSize={windowSize} />
      </div>
    </div>
  );
}

export default App;
